Declare
                            App integer;
                            appProduttore produttori%rowtype;
                            appSottoTipologia sottotipologie%rowtype;

                            ricerca varchar2(1500);

                            begin
                            -- Aggiornamento della chiave di ricerca
                            if Inserting Or Updating then
                            if :new.idProduttore is not null then
                            appProduttore := dao_select.produttori(:new.idProduttore);
                            end if;

                            if :new.idSottoTipologia is not null then
                            appSottoTipologia := dao_select.sottotipologie(:new.idSottoTipologia);
                            end if;

                            :New.RicercaDescrizione := substr(lib.getricerca(:New.Descrizione || :new.codicesillaro ||
                            :new.codiceproduttore || :new.codiciOEM || appProduttore.nominativo ||
                            Appsottotipologia.Nominativo), 1, 1500);
                            end if;

                            if inserting then
                            :new.dataInserimento := sysdate;

                            Select G.Famiglia
                            Into :new.famiglia
                            From Gruppi G, Tipologie T, Sottotipologie St
                            Where St.Idtipologia = T.Idtipologia And
                            T.Idgruppo = G.Idgruppo And
                            St.Idsottotipologia = :new.Idsottotipologia;
                            end if;

                            end;

Begin
                            -- Determino l'identificatore Cliente
                            If Inserting Then
                            If :New.IDCliente Is Null Then
                            Select seq_IDCliente.nextval Into :New.IDCliente From dual;
                            End If;
                            End if;

                            -- Aggiornamento della chiave di ricerca
                            If Inserting Or Updating Then
                            :New.RicercaRagioneSociale := Funzioni.Ricerca(:New.idgamma || :New.RagioneSociale ||
                            :new.PartitaIVA);
                            End If;
                            End;

Declare
                            App integer;

                            Begin

                            -- Controlli
                            If Inserting Or Updating Then
                            -- Determino l'identificatore articolo
                            If :New.DataInizioValidita > :New.DataFineValidita then
                            raise_application_error (-20001, 'La data fine validita'' deve essere maggiore della data di
                            inizio validita.');
                            End if;
                            End if;

                            end;

