                            App integer;

                            Begin
                            -- Controlli
                            If Inserting Or Updating Then
                            -- Determino l'identificatore articolo
                            If :New.DataInizioValidita > :New.DataFineValidita then
                            raise_application_error (-20001, 'La data fine validita'' deve essere maggiore della data di
