Declare
                            appOem oem%rowtype;
                            Begin
                            -- Controlli
                            If Inserting Then
                            -- Determino l'identificatore articolo
                            If :New.IDSistema Is Null Then
                            Select seq_IDSistema.nextval Into :New.IDSistema From dual;
                            End If;
                            End if;

                            -- Aggiornamento della chiave di ricerca
                            If (Inserting Or Updating) And :New.IdOem is not null Then
                            Appoem := Dao_Select.Oem(:New.Idoem);
                            :New.Ricercanominativo := Funzioni.Ricerca(Appoem.Nominativo || ' ' || :New.Nominativo);
                            end if;
                            End;

