package costanti is

                    MAX_DATA constant date := to_date('31/12/9999', 'dd/mm/yyyy');
                    MAX_CAPIENZA constant float := 999999999;

                    Ruoli_Utente Constant Varchar2(10) := 'E';
                    Ruoli_SuperUser Constant Varchar2(10) := 'A';

                    ALIQ_22 Constant Varchar2(10) := '22';

                    -- GRUPPI
                    GRP_MEMORIE constant integer := 1;
                    GRP_FLASH constant integer := 70;
                    GRP_BATTERIE constant integer := 4;
                    GRP_ALIMENTATORI constant integer := 90;

                    -- INCARICI
                    Inc_Amministrazione Constant Integer := 3;
                    INC_RESP_ACQUISTI constant integer := 7;

                    -- PRODUTTORI
                    PROD_FEDCO constant integer := 762;
                    PROD_DRBATTERY constant integer := 943;

                    -- PROVENIENZA
                    PROV_GESTIONALE Constant Varchar2(10) := 'G';
                    PROV_INTERNET Constant Varchar2(10) := 'I';
                    PROV_TELEFONO Constant Varchar2(10) := 'T';
                    PROV_MAIL constant varchar2(10) := 'M';
                    PROV_FAX constant varchar2(10) := 'F';

                    -- STATI ARTICOLO
                    STAT_ART_SHIPPING constant integer := 1;
                    STAT_ART_ON_HOLD constant integer := 5;
                    STAT_ART_LOCALE constant integer := 3;

                    -- STATI ORDINI
                    STAT_ACQUISITO Constant Varchar2(10) := 'A';
                    STAT_DA_EVADERE Constant Varchar2(10) := 'DE';
                    STAT_EVASO Constant Varchar2(10) := 'E';
                    STAT_CHIUSO constant varchar2(10) := 'C';

                    -- STATI RICHIESTA REGISTRAZIONE
                    Stat_Inviata Constant Varchar2(10) := 'I';
                    Stat_Accolta Constant Varchar2(10) := 'A';
                    Stat_Rifiutata Constant Varchar2(10) := 'R';

                    -- SCONTI
                    SCONTO_BASE0 constant integer := 49;
                    SCONTO_BASE1 constant integer := 50;
                    SCONTO_BASE2 constant integer := 60;
                    SCONTO_BASE3 constant integer := 70;

                    CRAWL_FEDCO constant varchar2(10) := 'FEDCO';
                    CRAWL_DRBATTERY constant varchar2(10) := 'MRBATT';

                    -- Produttori
                    Prod_Kaspersky constant integer := 580;


                    Function Getsconto_Base0 Return Integer;
                    Function Getsconto_Base1 Return Integer;
                    Function Getsconto_Base2 Return Integer;
                    Function Getsconto_Base3 Return Integer;

                    end;

