Package.SourceBody:
package body Dao_delete is
                    procedure Aforismi(oldRow in Itdistribuzione.Aforismi%rowtype) is
                    begin
                    Delete Itdistribuzione.Aforismi
                    Where Idaforisma = oldRow.Idaforisma;
                    end;

                    procedure Articoli(oldRow in Itdistribuzione.Articoli%rowtype) is
                    begin
                    Delete Itdistribuzione.Articoli
                    Where Idarticolo = oldRow.Idarticolo;
                    end;

                    procedure Articoliclienti(oldRow in Itdistribuzione.Articoliclienti%rowtype) is
                    begin
                    Delete Itdistribuzione.Articoliclienti
                    Where Idarticolo = oldRow.Idarticolo And
                    Idcliente = oldRow.Idcliente;
                    end;

                    procedure Articolifornitori(oldRow in Itdistribuzione.Articolifornitori%rowtype) is
                    begin
                    Delete Itdistribuzione.Articolifornitori
                    Where Idarticolo = oldRow.Idarticolo And
                    Idfornitore = oldRow.Idfornitore;
                    end;

                    procedure Barzellette(oldRow in Itdistribuzione.Barzellette%rowtype) is
                    begin
                    Delete Itdistribuzione.Barzellette
                    Where Idbarzelletta = oldRow.Idbarzelletta;
                    end;

                    procedure Carrello(oldRow in Itdistribuzione.Carrello%rowtype) is
                    begin
                    Delete Itdistribuzione.Carrello
                    Where Idcarrello = oldRow.Idcarrello;
                    end;

                    procedure Categorie09(oldRow in Itdistribuzione.Categorie09%rowtype) is
                    begin
                    Delete Itdistribuzione.Categorie09
                    Where Idcategoria09 = oldRow.Idcategoria09;
                    end;

                    procedure Causalireso(oldRow in Itdistribuzione.Causalireso%rowtype) is
                    begin
                    Delete Itdistribuzione.Causalireso
                    Where Idcausalereso = oldRow.Idcausalereso;
                    end;

                    procedure Clienti(oldRow in Itdistribuzione.Clienti%rowtype) is
                    begin
                    Delete Itdistribuzione.Clienti
                    Where Idcliente = oldRow.Idcliente;
                    end;

                    procedure Clienti_Produttori(oldRow in Itdistribuzione.Clienti_Produttori%rowtype) is
                    begin
                    Delete Itdistribuzione.Clienti_Produttori
                    Where Idcliente = oldRow.Idcliente And
                    Idproduttore = oldRow.Idproduttore;
                    end;

                    procedure Clienti_Tags(oldRow in Itdistribuzione.Clienti_Tags%rowtype) is
                    begin
                    Delete Itdistribuzione.Clienti_Tags
                    Where Idcliente = oldRow.Idcliente And
                    Idtag = oldRow.Idtag;
                    end;

                    procedure Cms(oldRow in Itdistribuzione.Cms%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms
                    Where Idcms = oldRow.Idcms;
                    end;

                    procedure Cms_Dec_Siti(oldRow in Itdistribuzione.Cms_Dec_Siti%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Dec_Siti
                    Where Codsito = oldRow.Codsito;
                    end;

                    procedure Cms_Dec_Stiliimmagini(oldRow in Itdistribuzione.Cms_Dec_Stiliimmagini%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Dec_Stiliimmagini
                    Where Codstileimmagine = oldRow.Codstileimmagine;
                    end;

                    procedure Cms_Dec_Stilipagine(oldRow in Itdistribuzione.Cms_Dec_Stilipagine%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Dec_Stilipagine
                    Where Codstilepagina = oldRow.Codstilepagina;
                    end;

                    procedure Cms_Dec_Stilisezioni(oldRow in Itdistribuzione.Cms_Dec_Stilisezioni%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Dec_Stilisezioni
                    Where Codstilesezione = oldRow.Codstilesezione;
                    end;

                    procedure Cms_Dec_Tipiimmagine(oldRow in Itdistribuzione.Cms_Dec_Tipiimmagine%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Dec_Tipiimmagine
                    Where Codtipoimmagine = oldRow.Codtipoimmagine;
                    end;

                    procedure Cms_Immagini(oldRow in Itdistribuzione.Cms_Immagini%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Immagini
                    Where Idimmagine = oldRow.Idimmagine;
                    end;

                    procedure Cms_Pagine(oldRow in Itdistribuzione.Cms_Pagine%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Pagine
                    Where Codpagina = oldRow.Codpagina;
                    end;

                    procedure Cms_Sezioni(oldRow in Itdistribuzione.Cms_Sezioni%rowtype) is
                    begin
                    Delete Itdistribuzione.Cms_Sezioni
                    Where Idsezione = oldRow.Idsezione;
                    end;

                    procedure Contatticlienti(oldRow in Itdistribuzione.Contatticlienti%rowtype) is
                    begin
                    Delete Itdistribuzione.Contatticlienti
                    Where Idcontattocliente = oldRow.Idcontattocliente;
                    end;

                    procedure Corrieri(oldRow in Itdistribuzione.Corrieri%rowtype) is
                    begin
                    Delete Itdistribuzione.Corrieri
                    Where Idcorriere = oldRow.Idcorriere;
                    end;

                    procedure Corsi(oldRow in Itdistribuzione.Corsi%rowtype) is
                    begin
                    Delete Itdistribuzione.Corsi
                    Where Idcorso = oldRow.Idcorso;
                    end;

                    procedure Crawled_Oem(oldRow in Itdistribuzione.Crawled_Oem%rowtype) is
                    begin
                    Delete Itdistribuzione.Crawled_Oem
                    Where Id = oldRow.Id;
                    end;

                    procedure Crawled_Sistemi(oldRow in Itdistribuzione.Crawled_Sistemi%rowtype) is
                    begin
                    Delete Itdistribuzione.Crawled_Sistemi
