package body Dao_insert is procedure Aforismi(newRow in Itdistribuzione.Aforismi%rowtype) is begin Insert Into Itdistribuzione.Aforismi (Idaforisma, Aforisma, Autore, Dataultimapublicazione) Values (newRow.Idaforisma, newRow.Aforisma, newRow.Autore, newRow.Dataultimapublicazione); end;
procedure Articoli(newRow in Itdistribuzione.Articoli%rowtype) is begin Insert Into Itdistribuzione.Articoli (Idarticolo, Codicesillaro, Codicioem, Codicebarre, Codiceproduttore, Idproduttore, Idsottotipologia, Idstatoarticolo, Idcategoria09, Garanzia, Descrizione, Messaggio, Tipomessaggio, Noteordine, Ricercadescrizione, Flagserialnumber, Flagserialnumberinterno, Idimmagine, Idcms, Pezziscatola, Flaglicenza, Datainserimento, Coddurata, Idarticoloold, Idarticolonew, Siae_Capienza, Siae_Codcategoria, Famiglia, Nomenclatura, Pesonetto, Pesolordo, Posizione, Flagobsoleto) Values (newRow.Idarticolo, newRow.Codicesillaro, newRow.Codicioem, newRow.Codicebarre, newRow.Codiceproduttore, newRow.Idproduttore, newRow.Idsottotipologia, newRow.Idstatoarticolo, newRow.Idcategoria09, newRow.Garanzia, newRow.Descrizione, newRow.Messaggio, newRow.Tipomessaggio, newRow.Noteordine, newRow.Ricercadescrizione, newRow.Flagserialnumber, newRow.Flagserialnumberinterno, newRow.Idimmagine, newRow.Idcms, newRow.Pezziscatola, newRow.Flaglicenza, newRow.Datainserimento, newRow.Coddurata, newRow.Idarticoloold, newRow.Idarticolonew, newRow.Siae_Capienza, newRow.Siae_Codcategoria, newRow.Famiglia, newRow.Nomenclatura, newRow.Pesonetto, newRow.Pesolordo, newRow.Posizione, newRow.Flagobsoleto); end;
procedure Articoliclienti(newRow in Itdistribuzione.Articoliclienti%rowtype) is begin Insert Into Itdistribuzione.Articoliclienti (Idarticolo, Idcliente, Codicecliente, Datainserimento) Values (newRow.Idarticolo, newRow.Idcliente, newRow.Codicecliente, newRow.Datainserimento); end;
procedure Articolifornitori(newRow in Itdistribuzione.Articolifornitori%rowtype) is begin Insert Into Itdistribuzione.Articolifornitori (Idarticolo, Idfornitore, Codicefornitore) Values (newRow.Idarticolo, newRow.Idfornitore, newRow.Codicefornitore); end;
procedure Barzellette(newRow in Itdistribuzione.Barzellette%rowtype) is begin Insert Into Itdistribuzione.Barzellette (Idbarzelletta, Titolo, Sottotitolo, Dataultimapublicazione, Testo) Values (newRow.Idbarzelletta, newRow.Titolo, newRow.Sottotitolo, newRow.Dataultimapublicazione, newRow.Testo); end;
