procedure Causalireso(newRow in Itdistribuzione.Causalireso%rowtype) is begin Insert Into Itdistribuzione.Causalireso (Idcausalereso, Nominativo, Flagresocliente, Flagresofornitore, Flagconteggio) Values (newRow.Idcausalereso, newRow.Nominativo, newRow.Flagresocliente, newRow.Flagresofornitore, newRow.Flagconteggio); end;
