Package.SourceBody:
package body Dao_Update is procedure Aforismi(oldRow in Itdistribuzione.Aforismi%rowtype, newRow in Itdistribuzione.Aforismi%rowtype) is begin Update Itdistribuzione.Aforismi Set Idaforisma = newRow.Idaforisma, Aforisma = newRow.Aforisma, Autore = newRow.Autore, Dataultimapublicazione = newRow.Dataultimapublicazione Where Idaforisma = oldRow.Idaforisma; end;
procedure Articoli(oldRow in Itdistribuzione.Articoli%rowtype, newRow in Itdistribuzione.Articoli%rowtype) is begin Update Itdistribuzione.Articoli Set Idarticolo = newRow.Idarticolo, Codicesillaro = newRow.Codicesillaro, Codicioem = newRow.Codicioem, Codicebarre = newRow.Codicebarre, Codiceproduttore = newRow.Codiceproduttore, Idproduttore = newRow.Idproduttore, Idsottotipologia = newRow.Idsottotipologia, Idstatoarticolo = newRow.Idstatoarticolo, Idcategoria09 = newRow.Idcategoria09, Garanzia = newRow.Garanzia, Descrizione = newRow.Descrizione, Messaggio = newRow.Messaggio, Tipomessaggio = newRow.Tipomessaggio, Noteordine = newRow.Noteordine, Ricercadescrizione = newRow.Ricercadescrizione, Flagserialnumber = newRow.Flagserialnumber, Flagserialnumberinterno = newRow.Flagserialnumberinterno, Idimmagine = newRow.Idimmagine, Idcms = newRow.Idcms, Pezziscatola = newRow.Pezziscatola, Flaglicenza = newRow.Flaglicenza, Datainserimento = newRow.Datainserimento, Coddurata = newRow.Coddurata, Idarticoloold = newRow.Idarticoloold, Idarticolonew = newRow.Idarticolonew, Siae_Capienza = newRow.Siae_Capienza, Siae_Codcategoria = newRow.Siae_Codcategoria, Famiglia = newRow.Famiglia, Nomenclatura = newRow.Nomenclatura, Pesonetto = newRow.Pesonetto, Pesolordo = newRow.Pesolordo, Posizione = newRow.Posizione, Flagobsoleto = newRow.Flagobsoleto Where Idarticolo = oldRow.Idarticolo; end;
procedure Articoliclienti(oldRow in Itdistribuzione.Articoliclienti%rowtype, newRow in Itdistribuzione.Articoliclienti%rowtype) is begin Update Itdistribuzione.Articoliclienti Set Idarticolo = newRow.Idarticolo, Idcliente = newRow.Idcliente, Codicecliente = newRow.Codicecliente, Datainserimento = newRow.Datainserimento Where Idarticolo = oldRow.Idarticolo And Idcliente = oldRow.Idcliente; end;
procedure Articolifornitori(oldRow in Itdistribuzione.Articolifornitori%rowtype, newRow in Itdistribuzione.Articolifornitori%rowtype) is begin Update Itdistribuzione.Articolifornitori Set Idarticolo = newRow.Idarticolo, Idfornitore = newRow.Idfornitore, Codicefornitore = newRow.Codicefornitore Where Idarticolo = oldRow.Idarticolo And Idfornitore = oldRow.Idfornitore; end;
procedure Barzellette(oldRow in Itdistribuzione.Barzellette%rowtype, newRow in Itdistribuzione.Barzellette%rowtype) is begin Update Itdistribuzione.Barzellette Set Idbarzelletta = newRow.Idbarzelletta, Titolo = newRow.Titolo, Sottotitolo = newRow.Sottotitolo, Dataultimapublicazione = newRow.Dataultimapublicazione, Testo = newRow.Testo Where Idbarzelletta = oldRow.Idbarzelletta; end;
procedure Carrello(oldRow in Itdistribuzione.Carrello%rowtype, newRow in Itdistribuzione.Carrello%rowtype) is begin Update Itdistribuzione.Carrello Set Idcarrello = newRow.Idcarrello, Idutente = newRow.Idutente, Idcliente = newRow.Idcliente, Data = newRow.Data, Descrizione = newRow.Descrizione Where Idcarrello = oldRow.Idcarrello; end;
