procedure Tipicms(oldRow in Itdistribuzione.Tipicms%rowtype, newRow in Itdistribuzione.Tipicms%rowtype) is begin Update Itdistribuzione.Tipicms Set Idtipocms = newRow.Idtipocms, Nominativo = newRow.Nominativo Where Idtipocms = oldRow.Idtipocms; end;
procedure Tipicontatto(oldRow in Itdistribuzione.Tipicontatto%rowtype, newRow in Itdistribuzione.Tipicontatto%rowtype) is begin Update Itdistribuzione.Tipicontatto Set Idtipocontatto = newRow.Idtipocontatto, Nominativo = newRow.Nominativo Where Idtipocontatto = oldRow.Idtipocontatto; end;
procedure Tipologie(oldRow in Itdistribuzione.Tipologie%rowtype, newRow in Itdistribuzione.Tipologie%rowtype) is begin Update Itdistribuzione.Tipologie Set Idtipologia = newRow.Idtipologia, Nominativo = newRow.Nominativo, Idgruppo = newRow.Idgruppo, Ordinamento = newRow.Ordinamento, Idimmagine = newRow.Idimmagine, Idcms = newRow.Idcms, Descrizione = newRow.Descrizione, Alias = newRow.Alias Where Idtipologia = oldRow.Idtipologia; end;
procedure Tipologiereso(oldRow in Itdistribuzione.Tipologiereso%rowtype, newRow in Itdistribuzione.Tipologiereso%rowtype) is begin Update Itdistribuzione.Tipologiereso Set Idtipologiareso = newRow.Idtipologiareso, Nominativo = newRow.Nominativo Where Idtipologiareso = oldRow.Idtipologiareso; end;
procedure Valute(oldRow in Itdistribuzione.Valute%rowtype, newRow in Itdistribuzione.Valute%rowtype) is begin Update Itdistribuzione.Valute Set Idvaluta = newRow.Idvaluta, Nominativo = newRow.Nominativo, Cambio = newRow.Cambio, Codgamma = newRow.Codgamma, Codinternazionale = newRow.Codinternazionale Where Idvaluta = oldRow.Idvaluta; end;
end;

