Package.SourceBody:
package body pck_articoli is

                    function getArticoloByCodSillaro (inCodiceSillaro in varchar2) return articoli%rowtype is
                    appArticoli articoli%rowtype;
                    begin
                    Select *
                    Into appArticoli
                    FROM ARTICOLI
                    Where codiceSillaro = trim(inCodiceSillaro);

                    return appArticoli;
                    exception
                    when no_data_found then
                    return null;
                    end;

                    function getArticoloByCodProduttore (inCodiceProduttore in varchar2) return articoli%rowtype is
                    appArticoli articoli%rowtype;
                    begin
                    Select *
                    Into appArticoli
                    From Articoli
                    Where codiceProduttore = trim(inCodiceProduttore);

                    return appArticoli;
                    exception
                    when no_data_found then
                    return null;
                    end;

                    Function Getlastarticoli Return Varchar2 Is
                    Cursor Carticoli Is
                    Select *
                    From Articoli
                    Where Idstatoarticolo = Costanti.Stat_Art_Shipping And
                    Idimmagine is not null
                    order by Datainserimento desc nulls last;

                    Appidproduttore Integer := 0;
                    i integer := 0;
                    result varchar2(200);
                    Begin
                    For rArticoli In cArticoli Loop
                    If Appidproduttore != Rarticoli.Idproduttore Then
                    result := Rarticoli.idarticolo || '|' || result;
                    I:= I +1;
                    Appidproduttore := Rarticoli.Idproduttore;
                    End if;

                    If I = 3 Then
                    return result;
                    End if;
                    End Loop;
                    end;
                    end;

