Package.SourceBody:
package body pck_Clienti is

                    -- Ritorna il fatturato dell'anno
                    function GetFatturato (InIDCliente in integer, InAnno in Integer) return float is
                    Result float;
                    begin
                    Select Sum (f.Importo)
                    Into Result
                    From View_Fatturato F
                    Where F.Datadocumento Between To_Date ('01/01/' || Inanno, 'dd/mm/yyyy') And To_Date ('31/12/' ||
                    Inanno, 'dd/mm/yyyy') And
                    F.Idcliente = InIDCliente;

                    Return Result;
                    end;

                    -- Ritorna il fatturato dell'anno per il produttore
                    function GetFatturato (InIDCliente in integer, InIDProduttore in integer, InAnno in Integer) return
                    float is
                    Result float;
                    begin
                    Select Sum (f.Importo)
                    Into Result
                    From Articoli A, View_Fatturato F
                    Where A.Idarticolo = F.Idarticolo And
                    F.Datadocumento Between To_Date ('01/01/' || Inanno, 'dd/mm/yyyy') And To_Date ('31/12/' || Inanno,
                    'dd/mm/yyyy') And
                    F.Idcliente = Inidcliente And
                    A.Idproduttore = InIDProduttore;

                    Return Result;
                    end;

                    Function GetOrdinato (Inidcliente In Integer, Inanno In Integer) Return Float IS
                    Result float;
                    begin
                    Select Sum (f.Importo)
                    Into Result
                    From view_Ordiniclienti F
                    Where F.DATA Between To_Date ('01/01/' || Inanno, 'dd/mm/yyyy') And To_Date ('31/12/' || Inanno,
                    'dd/mm/yyyy') And
                    F.Idcliente = InIDCliente;

                    Return Result;
                    end;


                    -- Verifica lo stato del cliente e ritorna:
                    -- W - Non ha mai acquistato
                    -- G - Acquista regolarmente
                    -- Y - Non acquista da 90 giorni
                    -- R - Non acquista da 1 anno
                    -- B - Non acquista da 2 anni
                    function GetStato (InIDCliente in integer) return varchar2 is
                    App Float;
                    Begin
                    Select trunc (sysdate) - Max (Data)
                    Into App
                    From Ordiniclienti f
                    Where IDCliente = InIDCliente;

                    If App is null then return 'W';
                    Elsif App <= 30 then return 'G';
                    Elsif App <= 90 then return 'Y';
                    Elsif App <= 365 then return 'R';
                    Else return 'B';
                    End if;
                    end;

                    -- Ritotrna la data ultimo ordine
                    Function Getultimoordine (Inidcliente In Integer) Return Date Is
                    App date;
                    Begin
                    Select Max (Data)
                    Into App
                    From Ordiniclienti
                    Where IDCliente = InIDCliente;

                    Return App;
                    End;

                    -- Ritotrna la data ultima offerta
                    Function GetultimaOfferta (Inidcliente In Integer) Return Date Is
                    App date;
                    Begin
                    Select Max (Data)
                    Into App
                    From Offerteclienti
                    Where IDCliente = InIDCliente;

                    Return App;
                    End;

                    -- Ritotrna la data ultimo contatto
                    Function GetultimoContatto (Inidcliente In Integer) Return Date Is
                    App date;
                    Begin
                    Select Max (Data)
                    Into App
                    From Contatticlienti
                    Where IDCliente = InIDCliente;

                    Return App;
                    End;

                    Function Getaliquota(Inidcliente In Integer) Return Float Is
                    result float;
                    Begin
                    Select A.ALIQUOTA
                    Into result
                    From Clienti C, Dec_Aliquote A
                    where c.codALiquota = a.codALiquota And
                    c.idcliente = Inidcliente;

                    return result;
                    end;

                    -- Verifica se il cliente è restrato su internet (S/N)
                    function isRegistrato (InIDCliente in integer) return varchar2 is
                    Result varchar2(1);
                    begin
                    Select 'S'
                    Into Result
                    From Sito_Utenti
                    Where IDCliente = InIDCliente And
                    rownum < 2;

                    Return result;
                    exception
                    when no_data_found then
                    return 'N';
                    end;

                    function getUltimoLogin (InIDCliente in integer) return date is
                    result date;
                    begin
                    Select max(su.dataultimologin)
                    Into result
                    from sito_utenti su
                    where su.idcliente = InIDCliente;

                    return result;
                    end;

                    function getResponsabileAcquisti (InIDCliente in integer) return varchar2 is
                    result varchar2(1000) := null;

                    Cursor cNominativi is
                    Select *
                    from nominativi n
                    where n.idcliente = InIDCliente And
                    n.idincarico = Costanti.INC_RESP_ACQUISTI;

                    begin
                    For rNominativi in cNominativi Loop
                    if result is not null then
                    result := result || ', ';
                    end if;

                    result := result || rNominativi.nominativo || '(' || rNominativi.postaelettronica || ')';
                    End Loop;

                    return result;
                    end;


                    Function Getemail (Inidcliente In Integer) Return Varchar2 Is
                    result varchar2(1000);

                    Cursor cUtenti is
                    Select *
                    From sito_Utenti
                    Where idCliente = InIDCliente;
                    begin
                    for rUtenti in cUtenti Loop
                    if (result is null) then
                    result := rUtenti.email;
                    else
                    result := result || '; ' || rUtenti.email;
                    end if;
                    End Loop;

                    return result;
                    end;

                    procedure aggiornaCodici (inIdCliente integer) is
                    cursor cArticoli is
                    Select *
                    From temp_articoliClienti;

                    appArticolo articoli%rowtype;
                    appArticoliClienti articoliclienti%rowtype;

                    begin
                    For rArticoli in cArticoli Loop
                    appArticolo := pck_articoli.getarticolobycodproduttore(rArticoli.codiceProduttore);

                    if appArticolo.Idarticolo is not null then
                    appArticoliClienti.Idarticolo := appArticolo.Idarticolo;
                    appArticoliClienti.Idcliente := inIdCliente;
                    appArticoliClienti.Codicecliente := rArticoli.CODICECLIENTE;

                    dao_update.articoliClienti(appArticoliClienti, appArticoliClienti);
                    if sql%rowcount = 0 then
                    dao_insert.articoliClienti(appArticoliClienti);
                    end if;
                    end if;
                    End Loop;
                    end;

                    Function Getcodice (Inidcliente Integer, Inidarticolo In Integer) return varchar2 Is
                    result articoliclienti.codicecliente%type;
                    Begin
                    Select codicecliente
                    into result
                    From Articoliclienti
                    Where Idcliente = Inidcliente And
                    Idarticolo = Inidarticolo;

                    return result;
                    Exception
                    When Others Then
                    return null;
                    end;

                    procedure aggiornaCodice (inIdCliente integer, inIdArticolo in integer, inCodiceCliente in varchar2)
                    is
                    appArticoliClienti articoliclienti%rowtype;
                    Begin
                    If Incodicecliente Is Null Then
                    Begin
                    Apparticoliclienti := Dao_Select.Articoliclienti(Inidarticolo, Inidcliente);
                    dao_delete.articoliclienti(Apparticoliclienti);
                    Exception
                    When No_Data_Found Then
                    Null;
                    End;
                    Else
                    Begin
                    Apparticoliclienti := Dao_Select.Articoliclienti(Inidarticolo, Inidcliente);

                    -- Aggiorno il codice
                    appArticoliClienti.Codicecliente := inCodiceCliente;
                    Dao_Update.Articoliclienti(Apparticoliclienti, Apparticoliclienti);

                    Exception
                    When No_Data_Found Then
                    -- Inserisco il codice
                    appArticoliClienti.Idarticolo := inIdArticolo;
                    appArticoliClienti.Idcliente := inIdCliente;
                    Apparticoliclienti.Codicecliente := Incodicecliente;
                    Apparticoliclienti.Datainserimento := Sysdate;

                    Dao_Insert.Articoliclienti(Apparticoliclienti);
                    End;
                    End If;
                    End;


                    function getMailAmministrazione (inIdCliente in integer) return varchar2 is
                    result varchar2(1000) := null;

                    Cursor cNominativi is
                    Select n.*
                    From Nominativi n
                    Where N.Idincarico = Costanti.Inc_Amministrazione And
                    n.idCliente = inIdCliente And
                    trim(n.postaelettronica) is not null
                    Order by idNominativo;
                    begin
                    For rNominativi in cNominativi Loop
                    if result is null then
                    Result := Rnominativi.Postaelettronica;
                    elsif length(result || ';' || rNominativi.postaelettronica) < 100 then
                    result := result || ';' || rNominativi.postaelettronica;
                    end if;
                    End Loop;

                    return result;
                    end;

                    Procedure Accogli (Inidrichiestaregistrazione In Integer) Is
                    appRichiestaregistrazione Richiestaregistrazione%rowtype :=
                    dao_select.Richiestaregistrazione(Inidrichiestaregistrazione);

                    Appcliente Clienti%Rowtype;
                    Appnominativo Nominativi%Rowtype;
                    Appdestinazione destinazioni%rowtype;

                    Begin
                    -- Cliente
                    Appcliente.Idcliente := Dao_Sequences.Seq_Idcliente;
                    Appcliente.Ragionesociale := Apprichiestaregistrazione.Ragionesociale;
                    Appcliente.Partitaiva := Apprichiestaregistrazione.Partitaiva;
                    Appcliente.Codicefiscale := Apprichiestaregistrazione.Codicefiscale;
                    Appcliente.Indirizzo := Apprichiestaregistrazione.Indirizzo;
                    Appcliente.Cap := Apprichiestaregistrazione.Cap;
                    Appcliente.Localita := Apprichiestaregistrazione.Localita;
                    Appcliente.Idprovincia := Apprichiestaregistrazione.Idprovincia;
                    Appcliente.Idgamma := Dao_Sequences.Seq_Idgammaclienti;

                    Appcliente.Codaliquota := Costanti.Aliq_22;
                    Appcliente.Fido := 1000;
                    Appcliente.Idpagamento := 90;
                    Appcliente.Flagbloccato := 'N';
                    Appcliente.Descrizioneblocco := '';

                    Appcliente.codSpesaTrasporto := '12';
                    Appcliente.idSconto := 50;
                    Appcliente.scontofinanziario := 0;

                    Appcliente.Codporto := 'FA';
                    Appcliente.Codvettore := 1032;
                    Appcliente.Codspedizione := 'VE';

                    Appcliente.idrichiestaregistrazione := Inidrichiestaregistrazione;

                    Dao_Insert.Clienti (Appcliente);

                    -- Nominativo Amministrazione
                    Appnominativo.Idnominativo := Dao_Sequences.Seq_Idnominativo;
                    Appnominativo.Idcliente := Appcliente.Idcliente;
                    Appnominativo.Idincarico := 3;
                    Appnominativo.Nominativo := Apprichiestaregistrazione.Nominativo;
                    Appnominativo.Telefono := Apprichiestaregistrazione.Telefono;
                    Appnominativo.Fax := Apprichiestaregistrazione.Fax;
                    Appnominativo.Postaelettronica := Apprichiestaregistrazione.Email;
                    Appnominativo.Flagescludimail := 'S';
                    Dao_Insert.Nominativi (Appnominativo);

                    -- Nominativo Responsabile Acquisti
                    Appnominativo.Idnominativo := Dao_Sequences.Seq_Idnominativo;
                    Appnominativo.Idcliente := Appcliente.Idcliente;
                    Appnominativo.Idincarico := 7;
                    Appnominativo.Nominativo := Apprichiestaregistrazione.Nominativo;
                    Appnominativo.Telefono := Apprichiestaregistrazione.Telefono;
                    Appnominativo.Fax := Apprichiestaregistrazione.Fax;
                    Appnominativo.Postaelettronica := Apprichiestaregistrazione.Email;
                    Appnominativo.Flagescludimail := 'N';
                    Dao_Insert.Nominativi (Appnominativo);

                    -- Destinazione principale
                    Appdestinazione.Iddestinazione := Dao_Sequences.Seq_Iddestinazione;
                    Appdestinazione.Idcliente := Appcliente.Idcliente;
                    Appdestinazione.Idgamma := PCK_Clienti.CalcIdDestinazioneGamma(Appcliente.Idcliente);
                    Appdestinazione.Flagprincipale := 'S';
                    If (Apprichiestaregistrazione.Sped_Denominazione Is Not Null Or
                    Apprichiestaregistrazione.Sped_Indirizzo Is Not Null) Then
                    Appdestinazione.Ragionesociale := Apprichiestaregistrazione.Sped_Denominazione;
                    Appdestinazione.Indirizzo := Apprichiestaregistrazione.SPED_INDIRIZZO;
                    Appdestinazione.Cap := Apprichiestaregistrazione.Sped_Cap;
                    Appdestinazione.Localita := Apprichiestaregistrazione.Sped_Localita;
                    Appdestinazione.Idlocalita := Null;
                    Appdestinazione.IDPROVINCIA := Apprichiestaregistrazione.Sped_Idprovincia;
                    else
                    Appdestinazione.Ragionesociale := Appcliente.Ragionesociale;
                    Appdestinazione.Indirizzo := Appcliente.Indirizzo;
                    Appdestinazione.Cap := Appcliente.Cap;
                    Appdestinazione.Localita := Appcliente.Localita;
                    Appdestinazione.Idlocalita := Null;
                    Appdestinazione.IDPROVINCIA := Appcliente.IDPROVINCIA;
                    End If;
                    Appdestinazione.Codporto := 'FA';
                    Appdestinazione.Codvettore := 1032;
                    Appdestinazione.Codspedizione := 'VE';
                    Dao_Insert.destinazioni(Appdestinazione);

                    -- Aggiorno lo stato della registrazione
                    Apprichiestaregistrazione.Codstatorichiesta := Costanti.Stat_Accolta;
                    dao_update.Richiestaregistrazione(Apprichiestaregistrazione, Apprichiestaregistrazione);

                    end;


                    Procedure Rifiuta (Inidrichiestaregistrazione In Integer) Is
                    appRichiestaregistrazione Richiestaregistrazione%rowtype :=
                    dao_select.Richiestaregistrazione(Inidrichiestaregistrazione);

                    Begin
                    -- Aggiorno lo stato della registrazione
                    Apprichiestaregistrazione.Codstatorichiesta := Costanti.Stat_Rifiutata;
                    dao_update.Richiestaregistrazione(Apprichiestaregistrazione, Apprichiestaregistrazione);
                    End;


                    Function CalcIdDestinazioneGamma(InIdCliente Integer) Return integer is
                    result integer;
                    begin
                    select count(*)
                    into result
                    from destinazioni
                    where idcliente = InIdCliente;

                    result := result + 1100; -- Il 1100 è stato aggiunto per retro compatibilità

                    return result;
                    end;

                    end;

