                    end;

Package.SourceBody:
package body pck_finacials is

                    procedure updateMovimentiContoCorrente is
                    Cursor cMovimenti is
                    Select *
                    from Movimenticontocorrente
                    Order by Idmovimentocontocorrente;

                    totale float;
                    begin

                    totale := 0;
                    For rMovimenti in cMovimenti Loop
                    totale := totale + nvl(rMovimenti.importo, 0);

                    rMovimenti.progressivoImporto := totale;
                    Dao_Update.Movimenticontocorrente(rMovimenti, rMovimenti);
                    End Loop;
                    end;

                    end;

Package.SourceSpec:
package pck_fornitori is

                    procedure popolaVenduto (inIdFornitore in integer);
                    procedure Popolavenduto;

                    end;

Package.SourceBody:
package body pck_fornitori is

                    function getSpecial (inIdArticolo in integer, inIdFornitore in integer) return righespecial%rowtype
                    is
                    Cursor cSpecial is
                    Select rs.*
                    from Special s, righespecial rs
                    where s.idSpecial = rs.idSpecial And
                    sysdate between s.data And nvl(s.datafinevalidita, Costanti.MAX_DATA) And
                    idArticolo = inIdArticolo And
                    idFornitore = inIdFornitore
                    Order by specialAcquisto desc;

                    begin
                    For rSpecial in cSpecial loop
                    return rSpecial;
                    End Loop;

                    return null;
                    end;

                    procedure insertVenduto (inIdFornitore in integer) is

                    Cursor cArticoli (inIdfornitore in integer) is
                    Select af.idarticolo,
                    Af.Idfornitore,
                    a.codicesillaro,
                    a.codiceproduttore,
                    af.codicefornitore,
                    a.descrizione,
                    a.pezziscatola,
                    trunc(f.Datadocumento, 'iw') data,
                    Pck_Prezzi.Getprezzoacquisto(Af.Idarticolo, Af.Idfornitore, Trunc(Sysdate)) Prezzo,
                    Sum(F.Quantita) Quantita
                    From View_Fatturato f, Articoli a, Articolifornitori af
                    Where af.idarticolo = a.idarticolo And
                    af.idarticolo = f.idarticolo (+) And
                    idFornitore = inIdfornitore And
                    f.Datadocumento (+) >= trunc(sysdate,'iw') - 7 * 8 And
                    a.idstatoarticolo in (1, 3)
                    Group By af.idarticolo,
                    af.idfornitore,
                    a.codicesillaro,
                    a.codiceproduttore,
                    af.codicefornitore,
                    a.descrizione,
                    A.Pezziscatola,
                    trunc(F.Datadocumento, 'iw'),
                    pck_prezzi.getprezzoacquisto(af.idarticolo, af.idfornitore, trunc(sysdate))
                    Order by af.idarticolo;

                    appArticoli temp_giacenzavenduto%rowtype := null;
                    appRigheSpecial righespecial%rowtype;
                    begin
                    for rArticoli in cArticoli(inIdFornitore) Loop
                    -- Primo record
                    if appArticoli.idArticolo is null then
                    appArticoli.Idarticolo := rArticoli.idArticolo;
                    appArticoli.Idfornitore := rArticoli.Idfornitore;
                    appArticoli.Codicesillaro := rArticoli.Codicesillaro;
                    appArticoli.CodiceProduttore := rArticoli.CodiceProduttore;
                    appArticoli.Codicefornitore := rArticoli.Codicefornitore;
                    appArticoli.Descrizione := rArticoli.Descrizione;
                    appArticoli.Pezziscatola := rArticoli.Pezziscatola;
                    appArticoli.Prezzo := rArticoli.prezzo;
                    end if;

                    -- Rottura su articolo
                    if appArticoli.idArticolo != rArticoli.idArticolo then
                    dao_insert.temp_giacenzavenduto(appArticoli);

                    appArticoli := null;
                    appArticoli.Idarticolo := rArticoli.Idarticolo;
                    appArticoli.Idfornitore := rArticoli.Idfornitore;
                    appArticoli.Codicesillaro := rArticoli.Codicesillaro;
                    appArticoli.CodiceProduttore := rArticoli.CodiceProduttore;
                    appArticoli.Codicefornitore := rArticoli.Codicefornitore;
                    appArticoli.Descrizione := rArticoli.Descrizione;
                    appArticoli.Pezziscatola := rArticoli.Pezziscatola;
                    appArticoli.Prezzo := rArticoli.prezzo;

                    appRigheSpecial := getSpecial(rArticoli.Idarticolo, inIdFornitore);
                    if appRigheSpecial.idArticolo is not null then
                    appArticoli.Note := 'Special: ' || appRigheSpecial.RIFERIMENTOSPECIAL || chr(13) || chr(10) ||
                    'Prezzo Acq: ' || appRigheSpecial.SPECIALACQUISTO || chr(13) || chr(10) ||
                    'Quantita'': ' || appRigheSpecial.QUANTITA;
                    end if;
                    end if;

                    if rArticoli.data = trunc(sysdate - 0*7, 'iw') then
                    appArticoli.Venduto_0 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 1*7, 'iw') then
                    appArticoli.Venduto_1 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 2*7, 'iw') then
                    appArticoli.Venduto_2 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 3*7, 'iw') then
                    appArticoli.Venduto_3 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 4*7, 'iw') then
                    appArticoli.Venduto_4 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 5*7, 'iw') then
                    appArticoli.Venduto_5 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 6*7, 'iw') then
                    appArticoli.Venduto_6 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 7*7, 'iw') then
                    appArticoli.Venduto_7 := rArticoli.quantita;
                    elsif rArticoli.data = trunc(sysdate - 8*7, 'iw') then
                    appArticoli.Venduto_8 := rArticoli.quantita;
                    end if;
                    end Loop;

                    if appArticoli.idArticolo is not null then
                    dao_insert.temp_giacenzavenduto(appArticoli);
                    end if;

                    end;

                    Procedure Updategiacenze Is
                    Cursor Cgiacenze Is
                    Select *
                    From Temp_Giacenzavenduto;

                    appGiacenza giacenze%rowtype;
                    Begin
                    For Rgiacenze In Cgiacenze Loop
                    begin
                    Appgiacenza := Dao_Select.giacenze (Rgiacenze.Idarticolo);

                    Rgiacenze.Giacenza := Appgiacenza.Giacenza;
                    Rgiacenze.Qtaspedizione := Appgiacenza.Appartato;
                    Rgiacenze.Qtaordinatacliente := Appgiacenza.Ordinatocliente;
                    Rgiacenze.Disponibilita := Appgiacenza.Disponibilita;
                    Rgiacenze.Qtaordinatafornitore := Appgiacenza.Ordinatofornitore;
                    Rgiacenze.Disponibilitafutura := Appgiacenza.Disponibilitafutura;
                    Exception
                    When Others Then
                    Rgiacenze.Giacenza := 0;
                    Rgiacenze.Qtaspedizione := 0;
                    Rgiacenze.Qtaordinatacliente := 0;
                    Rgiacenze.Disponibilita := 0;
                    Rgiacenze.Qtaordinatafornitore := 0;
                    Rgiacenze.Disponibilitafutura := 0;
                    end;

                    Dao_Update.Temp_Giacenzavenduto(Rgiacenze, Rgiacenze);
                    end Loop;
                    end;

                    Procedure Popolavenduto (Inidfornitore In Integer) Is
                    begin
                    Insertvenduto (Inidfornitore);
                    Updategiacenze;
                    end;

                    procedure Popolavenduto is
                    cursor cFornitori is
                    select *
                    from fornitori f
                    where f.flagattivo = 'S';

                    Begin
                    For rFornitori in cFornitori Loop
                    insertvenduto(rFornitori.idFornitore);
                    End Loop;
                    Updategiacenze;
                    End;

                    end;

Package.SourceSpec:
Package Pck_Gamma Is

                    Function GetdataGamma Return Date;

                    Procedure Aggiornadocumenti (Inidlogelaborazione In Integer);

                    end;

Package.SourceBody:
Package Body Pck_Gamma Is
Function GetdataGamma Return Date Is Begin return trunc(sysdate-75, 'mm'); end;
Procedure Aggiornadocumenti (Inidlogelaborazione In Integer) Is Cursor Cordini Is Select A.Idarticolo, D.* From Articoli A, Temp_Gamma_Documenti D Where A.Codicesillaro = D.Codice_Articolo And d.Tipo_Documento = 'CLI-ORDINE';
Maxiddocumento Integer; Data_gamma date;
Begin Select Min (Data_Documento) Into Data_Gamma From Temp_Gamma_Documenti;
If Data_Gamma Is Not Null Then -- Update righe Ordini Logging.Addinformation (Inidlogelaborazione, 'Aggiornamento Righe Ordini'); For Rordini In Cordini Loop Update Righeordiniclienti Roc Set Quantitaordinata = Rordini.Quantita_Documento, Quantitaconsegnata = Rordini.Quantita_Consegnata, Flagevaso = Rordini.Flagevaso Where Roc.Idordinecliente = Rordini.numero_documento And Roc.Idarticolo = Rordini.idarticolo; End Loop;
Logging.Addinformation (Inidlogelaborazione, 'Aggiornamento Testate Ordini'); Update Ordiniclienti Set Codstatoordine = 'E' Where Idordinecliente In (Select Idordinecliente From Righeordiniclienti Group By Idordinecliente Having Min(Flagevaso) = 'S');
-- Aggiornamento DDT Delete Righedocumenti Where Datadocumento >= Data_Gamma; Delete Documenti Where Datadocumento >= Data_Gamma;
Insert Into Documenti (Codtipodocumento, Numerodocumento, Datadocumento, Idcliente, Dataspedizione, Vettore) Select Distinct td.Codtipodocumento, D.Numero_Documento Numerodocumento, D.Data_Documento Datadocumento, C.Idcliente Idcliente, Data_Documento Dataspedizione, Null Vettore From Clienti C, Dec_Tipidocumento td, Temp_Gamma_Documenti D Where C.Idgamma = D.Codice_Cliente And Td.Codicegamma = D.Tipo_Documento; --d.Tipo_Documento in ('CLI-DDT', 'CLI-FATRIEPDDT', 'CLI-FATIMM');
-- DDT Insert Into Righedocumenti (Codtipodocumento, Numerodocumento, Datadocumento, Numeroriga, Idarticolo, Quantita, Importo, Idordinecliente) Select Distinct td.Codtipodocumento, D.Numero_Documento Numerodocumento, D.Data_Documento Datadocumento, D.Numero_Riga, A.Idarticolo, D.Quantita_Documento, D.Importo, D2.Numero_Documento From Temp_Gamma_Documenti D2, Articoli a, Clienti C, Dec_Tipidocumento td, Temp_Gamma_Documenti D Where C.Idgamma = D.Codice_Cliente And A.Codicesillaro = D.Codice_Articolo And Td.Codicegamma = D.Tipo_Documento And Td.Flagfattura = 'N' And -- d.Tipo_Documento in ('CLI-DDT') And D.Numero_Registrazione_Rif = D2.Numero_Registrazione;
-- CLI-FATRIEPDDT, CLI-FATIMM Insert Into Righedocumenti (Codtipodocumento, Numerodocumento, Datadocumento, Numeroriga, Idarticolo, Quantita, Importo, Idordinecliente) Select td.Codtipodocumento, D.Numero_Documento Numerodocumento, D.Data_Documento Datadocumento, D.Numero_Riga, A.Idarticolo, D.Quantita_Documento, D.Importo, null From Articoli a, Clienti C, Dec_Tipidocumento td, Temp_Gamma_Documenti D Where C.Idgamma = D.Codice_Cliente And A.Codicesillaro = D.Codice_Articolo And Td.Codicegamma = D.Tipo_Documento And Td.Flagfattura = 'S'; --And -- d.Tipo_Documento in ('CLI-DDT') And --D.Tipo_Documento In ('CLI-FATRIEPDDT', 'CLI-FATIMMDDT'); End If;
Exception When Others Then Logging.addError (Inidlogelaborazione, sqlerrm); end;
end;

Package.SourceSpec:
Package Pck_Giacenze Is

                    Procedure Cleardati;
                    Procedure Cleardatiproduttore (Inidproduttore In Integer);

                    Procedure Set (Incodicesillaro In Varchar2, inGiacenza Integer, inAppartato Integer,
                    inOrdinatocliente Integer, inOrdinatofornitore Integer);

                    Procedure Add (Incodiceproduttore In Varchar2, Inquantita In Integer, Indataaggiornamento In Date);
                    Function Get (Inidarticolo In Integer) Return Integer;

                    End;

Package.SourceBody:
Package Body Pck_Giacenze Is

                    Procedure InitGiacenze Is
                    Cursor cNewarticoli Is
                    Select Idarticolo
                    From Articoli
                    Minus
                    Select Idarticolo
                    From Giacenze;

                    Appgiacenza Giacenze%Rowtype;
                    Begin
                    -- Aggiungo gli articoli non ancora presenti
                    For Rnewarticoli In Cnewarticoli Loop
                    Appgiacenza.Idarticolo := Rnewarticoli.Idarticolo;
                    Appgiacenza.Giacenza := 0;
                    Appgiacenza.Appartato := 0;
                    Appgiacenza.Ordinatocliente := 0;
                    Appgiacenza.Disponibilita := 0;
                    Appgiacenza.Ordinatofornitore := 0;
                    Appgiacenza.Disponibilitafutura := 0;
                    Appgiacenza.Dataaggiornamento := Null;
                    Appgiacenza.Quantita := 0;

                    Dao_Insert.Giacenze(Appgiacenza);
                    End loop;
                    end;

                    Procedure Cleardati Is
                    Begin
                    -- Inizializzo la tabella
                    InitGiacenze;

                    -- Annullo le giacenze
                    Update Giacenze
                    Set Giacenza = 0,
                    Appartato = 0,
                    Ordinatocliente = 0,
                    Disponibilita = 0,
                    Ordinatofornitore = 0,
                    Disponibilitafutura = 0;
                    End;

                    Procedure Cleardatiproduttore (Inidproduttore In Integer) Is
                    Begin
                    -- Inizializzo la tabella
                    Initgiacenze;

                    -- Annullo le giacenze relative ad un determinato produttore
                    Update Giacenze
                    set Dataaggiornamento = Null,
                    Quantita = 0
                    Where idarticolo in (select idarticolo from articoli where idproduttore = inIdProduttore);
                    End;

                    Procedure Set (Incodicesillaro In Varchar2, Ingiacenza Integer, Inappartato Integer,
                    Inordinatocliente Integer, Inordinatofornitore Integer) Is
                    AppArticolo articoli%rowtype;
                    Begin
                    If Nvl(Ingiacenza, 0) != 0 Or Nvl(Inappartato, 0) != 0 Or Nvl(Inordinatocliente, 0) != 0 Or
                    Nvl(Inordinatofornitore, 0) != 0 Then
                    Apparticolo := Pck_Import.Getarticolobycodicesillaro(trim(Incodicesillaro));

                    If (Apparticolo.Idarticolo Is Not Null) Then
                    Update Giacenze
                    set Giacenza = Nvl(Ingiacenza, 0),
                    Appartato = Nvl(Inappartato, 0),
                    Ordinatocliente = Nvl(Inordinatocliente, 0),
                    Disponibilita = Nvl(Ingiacenza, 0) - Nvl(Inappartato, 0) - Nvl(Inordinatocliente, 0),
                    Ordinatofornitore = Nvl(Inordinatofornitore, 0),
                    Disponibilitafutura = Nvl(Ingiacenza, 0) - Nvl(Inappartato, 0) - Nvl(Inordinatocliente, 0) +
                    Nvl(Inordinatofornitore, 0)
                    where Idarticolo = Apparticolo.Idarticolo;
                    End If;
                    end if;
                    end;

                    Procedure Add (Incodiceproduttore In Varchar2, Inquantita In Integer, Indataaggiornamento In Date)
                    Is
                    appArticolo articoli%rowtype;
                    appGiacenza giacenze%rowtype;
                    begin
                    appArticolo := pck_import.getarticolobycodiceproduttore(inCodiceProduttore);

                    If (Apparticolo.Idarticolo Is Not Null) Then
                    Update Giacenze
                    Set Quantita = Nvl(Inquantita, 0),
                    Dataaggiornamento = Indataaggiornamento
                    where Idarticolo = Apparticolo.Idarticolo;
                    end if;
                    end;

                    function get (inIdArticolo in integer) return integer is
                    result integer;

                    begin
                    Select quantita
                    into result
                    from giacenze
                    where idArticolo = inIdArticolo;

                    exception
                    When No_Data_Found Then
                    return null;
                    End;
                    end;

Package.SourceSpec:
package pck_gruppi is

                    OBSOLETI constant varchar2(30) := 'Obsoleti';

                    Function Getarticoli (Inidsottotipologia In Integer) Return Integer;
                    Function Getarticoliattivi (Inidsottotipologia In Integer) Return Integer;
                    Function Getsottotipologie (Inidtipologia In Integer) Return Integer;
                    Function Getsottotipologieattive (Inidtipologia In Integer) Return Integer;
                    Function Gettipologie (Inidgruppo In Integer) Return Integer;
                    Function Gettipologieattive (Inidgruppo In Integer) Return Integer;

                    -- Sistema l'ordinamento delle sotto tipologie e tipologie
                    procedure classifica;

                    -- Crea una tipologia ed una sottotipologia "Obsoleti" per ogni gruppo e sposta sotto queste
                    categorie gli articolo on-hold
                    procedure riorganizza;

                    end;

Package.SourceBody:
package body pck_gruppi is

                    function getArticoli (inIdSottoTipologia in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    into result
                    from Articoli a
                    where a.idsottoTipologia = inIdSottoTipologia;

                    return result;
                    end;

                    function getArticoliAttivi (inIdSottoTipologia in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    into result
                    from Articoli a
                    where a.idsottoTipologia = inIdSottoTipologia And
                    a.idstatoarticolo = costanti.stat_art_shipping;

                    return result;
                    end;

                    function getSottoTipologie (inIdTipologia in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    into result
                    from SottoTipologie st
                    where st.idTipologia = inIdTipologia;

                    Return Result;
                    End;

                    function getSottoTipologieAttive (inIdTipologia in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    into result
                    from SottoTipologie st
                    where st.idTipologia = inIdTipologia And
                    st.ordinamento is not null;

                    return result;
                    End;

                    function getTipologie (inIdGruppo in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    into result
                    from Tipologie t
                    where t.idGruppo = inIdGruppo;

                    Return Result;
                    end;

                    function getTipologieAttive (inIdGruppo in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    into result
                    from Tipologie t
                    where t.idGruppo = inIdGruppo And
                    t.ordinamento is not null;

                    return result;
                    end;

                    procedure classifica is
                    procedure clearOrdinamentoSottoTipologie is
                    begin
                    Update SottoTipologie
                    set ordinamento = null
                    where pck_gruppi.getArticoliAttivi(idSottoTipologia) = 0;
                    end;

                    procedure clearOrdinamentoTipologie is
                    begin
                    Update Tipologie
                    set ordinamento = null
                    where pck_gruppi.getSottoTipologieAttive(idTipologia) = 0;
                    end;

                    -- Normalizzazione ordine Gruppi
                    procedure normalizzaGruppi is
                    Cursor cGruppi is
                    select g.*
                    from Gruppi g, dec_lineebusiness l
                    where g.codlineabusiness = l.codlineabusiness
                    order by l.posizione, g.ordinamento;

                    appOrdinamento integer := 10;
                    begin
                    For rGruppi in cGruppi Loop
                    if rGruppi.ordinamento is not null then
                    rGruppi.Ordinamento := Appordinamento;
                    Dao_Update.Gruppi (Rgruppi, Rgruppi);

                    Appordinamento := Appordinamento + 10;
                    end if;
                    End Loop;
                    end;

                    -- Normalizzazione ordine Tipologie
                    procedure normalizzaTipologie is
                    Cursor cTipologie is
                    select *
                    from Tipologie
                    order by idGruppo, ordinamento;

                    appTipologie cTipologie%rowtype;
                    appOrdinamento integer := 0;
                    begin
                    For rTipologie in cTipologie Loop
                    if nvl(rTipologie.idGruppo, 0) != nvl(appTipologie.idGruppo, 0) then
                    appOrdinamento := 10;
                    end if;

                    if rTipologie.ordinamento is not null then
                    rTipologie.ordinamento := appOrdinamento;
                    dao_update.tipologie (rTipologie, rTipologie);
                    end if;

                    appOrdinamento := appOrdinamento + 10;
                    appTipologie := rTipologie;
                    End Loop;
                    end;

                    -- Normalizzazione ordine SottoTipologie
                    procedure normalizzaSottoTipologie is
                    Cursor cSottoTipologie is
                    select *
                    from sottoTipologie
                    order by idTipologia, ordinamento;

                    appSottoTipologie cSottoTipologie%rowtype;
                    appOrdinamento integer := 0;
                    begin
                    For rSottoTipologie in cSottoTipologie Loop
                    if nvl(rSottoTipologie.idTipologia, 0) != nvl(appSottoTipologie.idTipologia, 0) then
                    appOrdinamento := 10;
                    end if;

                    if rSottoTipologie.ordinamento is not null then
                    rSottoTipologie.ordinamento := appOrdinamento;
                    dao_update.sottoTipologie (rSottoTipologie, rSottoTipologie);
                    end if;

                    appOrdinamento := appOrdinamento + 10;
                    appSottoTipologie := rSottoTipologie;
                    End Loop;
                    end;

                    begin
                    clearOrdinamentoSottoTipologie;
                    clearOrdinamentoTipologie;

                    normalizzaGruppi;
                    normalizzaTipologie;
                    normalizzaSottoTipologie;
                    end;

                    Procedure Riorganizza Is
                    appIdTipologia integer;
                    appIdSottoTipologia integer;

                    Cursor cGruppi is
                    Select *
                    From Gruppi
                    order by Ordinamento;

                    Cursor Carticoliobsoleti (inIdGruppo in integer, inIDSottoTipologiaObsoleti in integer) Is
                    Select *
                    From View_Articoli_Class Va
                    Where Va.Idgruppo = Inidgruppo And
                    Va.Idstatoarticolo = Costanti.Stat_Art_On_Hold And
                    Va.IDSottoTipologia != inIDSottoTipologiaObsoleti;

                    function creaTipologia (inIdGruppo in integer) return integer is
                    appTipologia Tipologie%rowtype;
                    begin
                    select min(idTipologia)
                    into appTipologia.idTipologia
                    From Tipologie
                    where idGruppo = inIdGruppo And
                    Nominativo = OBSOLETI;

                    if appTipologia.idTipologia is null then
                    appTipologia.Idtipologia := dao_sequences.seq_idtipologia;
                    appTipologia.Nominativo := OBSOLETI;
                    appTipologia.Idgruppo := inIdGruppo;

                    dao_insert.tipologie (appTipologia);
                    end if;

                    return appTipologia.Idtipologia;
                    end;

                    function creaSottoTipologia (inIdTipologia in integer) return integer is
                    appSottoTipologia SottoTipologie%rowtype;
                    begin
                    select min(idSottoTipologia)
                    into appSottoTipologia.idSottoTipologia
                    from SottoTipologie
                    where IdTipologia = inIdTipologia And
                    Nominativo = OBSOLETI;

                    if appSottoTipologia.idSottoTipologia is null then
                    appSottoTipologia.IdSottotipologia := dao_sequences.seq_idSottotipologia;
                    appSottoTipologia.Nominativo := OBSOLETI;
                    appSottoTipologia.Idtipologia := inIdTipologia;

                    dao_insert.Sottotipologie (appSottoTipologia);
                    end if;

                    return appSottoTipologia.idSottoTipologia;
                    end;


                    begin
                    -- Riclassifico gli articoli obsoleti
                    For Rgruppi In Cgruppi Loop
                    appIdTipologia := creaTipologia (rGruppi.Idgruppo);
                    appIdSottoTipologia := creaSottoTipologia (appIdTipologia);

                    For Rarticoliobsoleti In Carticoliobsoleti(rGruppi.idGruppo, appIdSottoTipologia) Loop
                    Update Articoli
                    Set Idsottotipologia = Appidsottotipologia
                    Where idArticolo = Rarticoliobsoleti.idArticolo;
                    End Loop;
                    /*
                    Update articoli
                    set idSottoTipologia = appIdSottoTipologia
                    where idStatoarticolo = Costanti.STAT_ART_ON_HOLD and
                    idsottotipologia in (select idsottotipologia
                    from tipologie t, sottotipologie st
                    Where T.Idtipologia = St.Idtipologia And
                    t.idgruppo = rGruppi.idgruppo);*/

                    End Loop;

                    -- Elimino le SottoTipologie inutili
                    delete sottotipologie
                    where idsottotipologia in (select idsottotipologia from sottotipologie
                    minus
                    select idsottotipologia from articoli);

                    -- Elimino le Tipologie inutili
                    delete tipologie
                    where idTipologia in (select idTipologia from Tipologie
                    minus
                    select idtipologia from SottoTipologie);
                    end;

                    end;

