Package.SourceBody:
package body pck_import is

                    function getArticoloByCodiceSillaro (inCodiceSillaro in varchar2) return articoli%rowtype is
                    Result articoli%rowtype;
                    Begin
                    Select *
                    Into Result
                    From Articoli
                    Where upper(CodiceSillaro) = upper(inCodiceSillaro);

                    return result;
                    exception
                    when no_data_found then
                    return null;
                    end;


                    function getArticoloByCodiceProduttore (inCodiceProduttore in varchar2) return articoli%rowtype is
                    Result Articoli%Rowtype;
                    begin
                    Select *
                    Into Result
                    From Articoli
                    Where upper(CodiceProduttore) = upper(inCodiceProduttore);

                    return result;
                    exception
                    when no_data_found then
                    return null;
                    end;

                    function getSottoTipologia (inIDLogElaborazione in integer, inTipologia in varchar2,
                    inSottoTipologia in varchar2) return sottotipologie%rowtype is
                    Result sottotipologie%rowtype;
                    begin
                    if inSottoTipologia is null then
                    return null;
                    end if;

                    If (Intipologia Is Null) Then
                    Select *
                    Into Result
                    From sottotipologie
                    Where Upper(Nominativo) = Upper(Insottotipologia);
                    Else
                    Select st.*
                    Into Result
                    From Sottotipologie St, Tipologie T
                    Where t.idtipologia = st.idtipologia And
                    Upper(St.Nominativo) = Upper(Insottotipologia) And
                    Upper(t.Nominativo) = Upper(inTipologia);
                    end if;

                    return result;
                    exception
                    when no_data_found then
                    Logging.addwarning(inIDLogElaborazione, ' Sotto tipologia non trovata: ' || inSottoTipologia);
                    return null;
                    when too_many_rows then
                    logging.addwarning(inidlogelaborazione, ' Sotto tipologia duplicata: ' || insottotipologia);
                    return null;
                    End;

                    function getSottoTipologiaByAlias (inIDLogElaborazione in integer, inAliasTipologia in varchar2,
                    inAliasSottoTipologia in varchar2) return sottotipologie%rowtype is
                    Result sottotipologie%rowtype;
                    Begin
                    if inAliasTipologia is null Or inAliasSottoTipologia is null then
                    return null;
                    end if;

                    Select st.*
                    Into Result
                    From Sottotipologie St, Tipologie T
                    Where T.Idtipologia = St.Idtipologia And
                    Upper(St.Alias) = Upper(Inaliassottotipologia) And
                    Upper(t.Alias) = Upper(inAliasTipologia);

                    return result;
                    exception
                    When No_Data_Found Then
                    Logging.addwarning(inIDLogElaborazione, ' Sotto tipologia non trovata: ' || inAliasSottoTipologia);
                    return null;
                    when too_many_rows then
                    logging.addwarning(inidlogelaborazione, ' Sotto tipologia duplicata: ' || inAliasTipologia);
                    return null;
                    End;

                    function getStatoArticolo (inIDLogElaborazione in integer, inStatoArticolo in varchar2) return
                    statiarticolo%rowtype is
                    Result statiarticolo%rowtype;
                    begin
                    if inStatoArticolo is null then
                    return null;
                    end if;

                    Select *
                    Into Result
                    From statiarticolo
                    Where upper(nominativo) = upper(inStatoArticolo);

                    return result;
                    exception
                    when no_data_found then
                    Logging.addinformation(inIDLogElaborazione, ' Stato articolo non trovato: ' || inStatoArticolo);
                    return null;
                    end;

                    function getCategoria09 (inIDLogElaborazione in integer, inCategoria09 in varchar2) return
