                    function importPrezzoSiae (inArticolo articoli%rowtype, inDataInizioValidita in date,
                    inPrezzoAcquisto in float, inCambio in float, inCosti in float, inMargineNetto in float,
                    inMargineLordo in float) return varchar2 is
                    appPrezzoVendita float;
                    begin
                    appPrezzoVendita := inPrezzoAcquisto / nvl(inCambio, 1);
                    appPrezzoVendita := appPrezzoVendita / (100 - nvl(inCosti, 0)) * 100;
                    appPrezzoVendita := appPrezzoVendita / (100 - nvl(inMargineNetto, 0)) * 100;
                    appPrezzoVendita := appPrezzoVendita / (100 - nvl(inMargineLordo, 0)) * 100;
                    appPrezzoVendita := appPrezzoVendita + nvl(pck_prezzi.getcostosiae(inArticolo.siae_codCategoria,
                    inArticolo.siae_capienza, inDataInizioValidita), 0);
                    appPrezzoVendita := round(appPrezzoVendita, 2);

                    pck_prezzi.insertPrezzoVendita(inArticolo.idArticolo, inDataInizioValidita, null,
                    round(appPrezzoVendita, 2), null);
                    return 'Aggiornato correttamente - Prz. Ven: ' || appPrezzoVendita;
                    end;

                    function importPrezzoSiae (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2,
                    inDataInizioValidita in date, inPrezzoAcquisto in float, inIdFornitore in integer) return varchar2
                    is
                    appArticolo articoli%rowtype;
                    appPrezzoVendita float;

                    appFornitoriMargini FornitoriMargini%rowtype;
                    appFornitori Fornitori%rowtype;

                    appCambio float;
                    appCosti float;
                    appMargineNetto float;
                    appMargineLordo float;
                    begin
                    if inCodiceSillaro is not null then
                    AppArticolo := getarticolobycodicesillaro(inCodiceSillaro);
                    elsif inCodiceProduttore is not null then
                    AppArticolo := getarticolobycodiceproduttore(inCodiceProduttore);
                    end if;

                    if Apparticolo.Idarticolo is null then
                    return 'Impossibile trovare l''articolo';
                    end if;

                    appFornitori := dao_select.fornitori(inIdFornitore);
                    appCambio := Dao_Select.Valute(appFornitori.idValuta).Cambio;
                    begin
                    appFornitoriMargini := dao_select.fornitorimargini(inIdFornitore, AppArticolo.idproduttore);
                    appCosti := appFornitoriMargini.Perccosti;
                    appMargineNetto := appFornitoriMargini.Percmarginenetto;
                    appMargineLordo := appFornitoriMargini.Percmarginelordo;
                    exception
                    when no_data_found then
                    appCosti := appFornitori.Perccosti;
                    appMargineNetto := appFornitori.Percmarginenetto;
                    appMargineLordo := appFornitori.Percmarginelordo;
                    end;

                    return importPrezzoSiae (Apparticolo, inDataInizioValidita, inPrezzoAcquisto, appCambio, appCosti,
                    appMargineNetto, appMargineLordo);
                    end;

                    function importPrezzoSiae (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2,
                    inDataInizioValidita in date, inPrezzoAcquisto in float, inCambio in float, inCosti in float,
                    inMargineNetto in float, inMargineLordo in float) return varchar2 is
                    appArticolo articoli%rowtype;
                    appPrezzoVendita float;
                    begin
                    if inCodiceSillaro is not null then
                    AppArticolo := getarticolobycodicesillaro(inCodiceSillaro);
                    elsif inCodiceProduttore is not null then
                    AppArticolo := getarticolobycodiceproduttore(inCodiceProduttore);
                    end if;

                    if Apparticolo.Idarticolo is null then
                    return 'Impossibile trovare l''articolo';
                    end if;

                    return importPrezzoSiae (Apparticolo, inDataInizioValidita, inPrezzoAcquisto, inCambio, inCosti,
                    inMargineNetto, inMargineLordo);
                    end;

                    function importPrezzoVendita (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2, inPrezzo
                    in float, inDataInizioValidita in date) return varchar2 is
                    AppArticolo articoli%rowtype;
                    begin
                    if inCodiceSillaro is not null then
                    AppArticolo := getarticolobycodicesillaro(inCodiceSillaro);
                    elsif inCodiceProduttore is not null then
                    AppArticolo := getarticolobycodiceproduttore(inCodiceProduttore);
                    end if;

                    if Apparticolo.Idarticolo is null then
                    return 'Impossibile trovare l''articolo';
                    end if;

                    pck_prezzi.insertPrezzoVendita(Apparticolo.Idarticolo, inDataInizioValidita, null, inPrezzo, null);

                    return 'Aggiornato correttamente';
                    end;


                    function importPrezzoVendita (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2, inPrezzo
                    in float) return varchar2 is
                    begin
                    return importPrezzoVendita (inCodiceSillaro, inCodiceProduttore, inPrezzo, trunc(sysdate));
                    end;

                    function importPrezzoVenditaNetto (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2,
                    inPrezzoNetto in float, inDataInizioValidita in date) return varchar2 is
                    AppArticolo articoli%rowtype;
                    begin
                    if inCodiceSillaro is not null then
                    AppArticolo := getarticolobycodicesillaro(inCodiceSillaro);
                    elsif inCodiceProduttore is not null then
                    AppArticolo := getarticolobycodiceproduttore(inCodiceProduttore);
                    end if;

                    if Apparticolo.Idarticolo is null then
                    return 'Impossibile trovare l''articolo';
                    end if;

                    pck_prezzi.insertPrezzoVendita(Apparticolo.Idarticolo, inDataInizioValidita, null, null,
                    inPrezzoNetto);

                    return 'Aggiornato correttamente';
                    end;


                    function importPrezzoVenditaNetto (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2,
                    inPrezzoNetto in float) return varchar2 is
                    begin
                    return importPrezzoVenditaNetto (inCodiceSillaro, inCodiceProduttore, inPrezzoNetto,
                    trunc(sysdate));
                    end;

                    function importPrezzoAcquisto (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2,
                    inIDFornitore in integer, inPrezzo in float, inDataInizioValidita in date) return varchar2 is
                    AppArticolo articoli%rowtype;
                    app integer;
                    begin
                    if inCodiceSillaro is not null then
                    AppArticolo := getarticolobycodicesillaro(inCodiceSillaro);
                    elsif inCodiceProduttore is not null then
                    AppArticolo := getarticolobycodiceproduttore(inCodiceProduttore);
                    end if;

                    if Apparticolo.Idarticolo is null then
                    return 'Impossibile trovare l''articolo';
                    end if;


                    Select count(*)
                    Into app
                    From articolifornitori af
                    Where af.idarticolo = Apparticolo.Idarticolo And
                    af.idfornitore = inIDFornitore;

                    if app = 0 then
                    return 'Articolo non associato al fornitore';
                    end if;

                    pck_prezzi.insertPrezzoAcquisto(Apparticolo.Idarticolo, inIDFornitore, inDataInizioValidita, null,
                    round(inPrezzo, 2), null);

                    return 'Aggiornato correttamente';
                    end;


                    function importPrezzoAcquisto (inCodiceSillaro in varchar2, inCodiceProduttore in varchar2,
                    inIDFornitore in integer, inPrezzo in float) return varchar2 is
                    Begin
                    return importPrezzoAcquisto (inCodiceSillaro, inCodiceProduttore, inIDFornitore, inPrezzo,
                    trunc(sysdate));
                    end;

                    function getIDOEM (InNominativo in varchar2) return integer is
                    Result integer;

                    begin
                    Select IDOEM
                    Into Result
                    From NominativiOEM
                    Where Nominativo = Upper(InNominativo);

                    Return Result;
                    exception
                    when others then
                    return null;
                    end;

                    -- Recupera l'IDArticolo partendo dal codice produttore
                    function getIDArticolo (InCodiceProduttore in varchar2) return integer is
                    Result Integer;
                    begin
                    Select min(IDArticolo)
                    Into Result
                    From Articoli
                    Where CodiceProduttore = InCodiceProduttore;

                    if Result is null then
                    Select min(IDArticolo)
                    Into Result
                    From Articoli
                    Where Upper(CodiceProduttore) = Upper(InCodiceProduttore);
                    end if;

                    Return Result;
                    end;

                    function getSistema (InNominativo in varchar2) return sistemi%rowtype is
                    Result sistemi%rowtype;
                    begin
                    Select *
                    Into Result
                    From Sistemi
                    Where Upper(Nominativo) = Upper(Innominativo) And
                    rownum < 2;

                    Return Result;
                    exception
                    when others then
                    return null;
                    end;

                    function existsSistemaArticolo (inIdSistema in integer, inIdArticolo in integer) return boolean is
                    app integer;
                    begin
                    Select count(*)
                    into app
                    from sistemiarticoli
                    where idArticolo = inIdArticolo And
                    idSistema = inIdSistema;

                    return app = 1;
                    end;

                    function existsSchedaTecnica (inIdArticolo in integer, inDescrizione in varchar2) return boolean is
                    app integer;
                    begin
                    Select count(*)
                    into app
                    from schedetecniche
                    where idArticolo = inIdArticolo And
                    Descrizione = inDescrizione;

                    return app = 1;
                    end;

                    procedure KingstonClear is
                    begin
                    Execute Immediate 'truncate table Kingston_Brandeqpn';
                    Execute Immediate 'truncate table Kingston_Cpuchipsets';
                    Execute Immediate 'truncate table Kingston_Products';
                    Execute Immediate 'truncate table Kingston_Sysgroups';
                    Execute Immediate 'truncate table Kingston_Sysmemfeatures';
                    Execute Immediate 'truncate table Kingston_Sysnotes';
                    Execute Immediate 'truncate table Kingston_Sysparts';
                    Execute Immediate 'truncate table Kingston_Sysschema';
                    Execute Immediate 'truncate table Kingston_Systems';
                    end;

                    procedure KingstonToImport is
                    Cursor cKingston1 Is
                    Select rowid id,
                    k.*
                    From Kingston_SysNotes k
                    Order By GroupID, NoteType, NoteOrder;

                    Cursor cKingston2 Is
                    Select rowid id,
                    k.*
                    From Kingston_SysSchema k
                    Order By GroupID, Schemano;


                    oldKingston1 cKingston1%rowtype;
                    oldKingston2 cKingston2%rowtype;
                    Begin
                    -- Prepare 1
                    For currKingston in cKingston1 Loop
                    If oldKingston1.groupid is null Then
                    oldKingston1 := currKingston;
                    Elsif oldKingston1.groupid != currKingston.groupid Or oldKingston1.NoteType != currKingston.NoteType
                    then
                    Update Kingston_SysNotes Set Notes = oldKingston1.Notes Where rowid = oldKingston1.id;
                    oldKingston1 := currKingston;
                    Else
                    oldKingston1.notes := substr (oldKingston1.notes || chr(13) || currKingston.notes, 1, 2048);
                    delete Kingston_SysNotes where rowid = currKingston.id;
                    End If;
                    End Loop;

                    Update Kingston_SysNotes Set Notes = oldKingston1.Notes Where rowid = oldKingston1.id;

                    -- Prepare 2
                    For currKingston in cKingston2 Loop
                    If oldKingston2.groupid is null Then
                    oldKingston2 := currKingston;
                    Elsif oldKingston2.groupid != currKingston.groupid then
                    Update Kingston_SysSchema Set sch = oldKingston2.sch Where rowid = oldKingston2.id;
                    oldKingston2 := currKingston;
                    Else
                    oldKingston2.sch := substr (oldKingston2.sch || chr(13) || currKingston.sch, 1, 2048);
                    delete Kingston_SysSchema where rowid = currKingston.id;
                    End If;
                    End Loop;

                    Update Kingston_SysSchema Set sch = oldKingston2.sch Where rowid = oldKingston2.id;

                    -- Import articoli
                    Insert Into IMPORT_ARTICOLI (CODICEPRODUTTORE, DESCRIZIONE)
                    Select PRODUCTNUMB, PRODUCTDESC
                    From KINGSTON_Products;

                    -- Aggiorno i Codici OEM
                    For rCodiceoem In (Select * From Kingston_Brandeqpn) Loop
                    Update Import_Articoli
                    Set Codicioem = Decode(Codicioem, Null, Rcodiceoem.Brandequivpn, Codicioem || ', ' ||
                    Rcodiceoem.Brandequivpn)
                    Where codiceproduttore = Rcodiceoem.Productnumb;
                    End Loop;

                    -- Import OEM
                    Insert into IMPORT_OEM (NOMINATIVO)
                    Select distinct makeName OEM
                    From kingston_systems;

                    -- Import sistemi
                    Insert into IMPORT_SISTEMI (OEM, NOMINATIVO, DESCRIZIONE, memoriastandard, memoriaespansione,
                    memoriamassima, BANCHI, UPGRADE, COMMENTI)
                    Select makeName OEM,
                    modelName || ' ' || modelNumber Nominativo,
                    sysType Descrizione,
                    MemStandard.description,
                    MemExpansion.description,
                    MemMaxAllowed.description,
                    ss.SCH,
                    upgrade.notes,
                    install.notes
                    From kingston_sysmemfeatures MemExpansion,
                    kingston_sysmemfeatures MemStandard,
                    kingston_sysmemfeatures MemMaxAllowed,
                    Kingston_SysSchema ss,
                    Kingston_SysNotes upgrade,
                    Kingston_SysNotes install,
                    kingston_sysgroups sg,
                    kingston_systems s
                    Where s.makeid = sg.makeid And
                    s.sysgroup = sg.sysgroup And
                    ss.Groupid (+) = sg.Groupid And
                    upgrade.Groupid (+) = sg.Groupid And
                    upgrade.NoteType (+) = 'UPGRADE' And
                    install.Groupid (+) = sg.Groupid And
                    install.NoteType (+) = 'INSTALL' And
                    MemExpansion.Groupid (+) = sg.Groupid And
                    MemExpansion.feature (+) = 'MemExpansion' And
                    MemMaxAllowed.Groupid (+) = sg.Groupid And
                    MemMaxAllowed.feature (+) = 'MemMaxAllowed' And
                    MemStandard.Groupid (+) = sg.Groupid And
                    MemStandard.feature (+) = 'MemStandard';

                    -- Import sistemi articoli
                    Insert into IMPORT_SISTEMIARTICOLI (ARTICOLO, SISTEMA)
                    Select PRODUCTNUMB, modelName || ' ' || modelNumber
                    From kingston_sysparts sp, kingston_sysgroups sg, kingston_systems s
                    Where s.makeid = sg.makeid And
                    s.sysgroup = sg.sysgroup And
                    sg.groupId = sp.groupId
                    Order by 2;

                    -- Schede tecniche
                    Insert Into Import_Schedetecniche
                    (Articolo, Descrizione, Valore, Ordinamento)
                    Select Productnumb, 'Tipologia', Prodline, 10
                    From Kingston_Products;

                    Insert Into Import_Schedetecniche
                    (Articolo, Descrizione, Valore, Ordinamento)
                    Select Productnumb, 'Capacità', Capacity || ' ' || Memunit, 20
                    From Kingston_Products;

                    Insert Into Import_Schedetecniche
                    (Articolo, Descrizione, Valore, Ordinamento)
                    Select Productnumb, 'Form factor', Moduletype || ' ' || Formfactor || ' ' || Dimmtype, 30
                    From Kingston_Products;

                    Insert Into Import_Schedetecniche
                    (Articolo, Descrizione, Valore, Ordinamento)
                    Select Productnumb, 'Pin', pin, 40
                    From Kingston_Products;

                    Insert Into Import_Schedetecniche
                    (Articolo, Descrizione, Valore, Ordinamento)
                    Select Productnumb, 'Frequenza', Speed, 50
                    From Kingston_Products;

                    Insert Into Import_Schedetecniche
                    (Articolo, Descrizione, Valore, Ordinamento)
                    Select Productnumb, 'Numero pezzi', Numbofpiece, 60
                    From Kingston_Products
                    Where Memunit Is Not Null;
                    End;

                    procedure KingstonAnalyze is
                    Begin
                    Execute Immediate 'analyze table Kingston_Brandeqpn compute statistics';
                    Execute Immediate 'analyze table Kingston_Cpuchipsets compute statistics';
                    Execute Immediate 'analyze table Kingston_Products compute statistics';
                    Execute Immediate 'analyze table Kingston_Sysgroups compute statistics';
                    Execute Immediate 'analyze table Kingston_Sysmemfeatures compute statistics';
                    Execute Immediate 'analyze table Kingston_Sysnotes compute statistics';
                    Execute Immediate 'analyze table Kingston_Sysparts compute statistics';
                    Execute Immediate 'analyze table Kingston_Sysschema compute statistics';
                    Execute Immediate 'analyze table Kingston_Systems compute statistics';
                    end;

                    procedure clear is
                    begin
                    execute immediate 'truncate table IMPORT_ARTICOLI';
                    execute immediate 'truncate table IMPORT_OEM';
                    execute immediate 'truncate table IMPORT_SCHEDETECNICHE';
                    execute immediate 'truncate table IMPORT_SISTEMI';
                    execute immediate 'truncate table IMPORT_SISTEMIARTICOLI';
                    execute immediate 'truncate table IMPORT_ARTICOLICLIENTI';
                    end;

                    procedure deleteSistemiArticoli (inIDLogElaborazione in integer, inIDProduttore in integer) is
                    begin
                    -- Pulizia legami sistemi articoli
                    Logging.addinformation(inIDLogElaborazione, ' Pulizia legami sistemi articoli');
                    delete sistemiarticoli
                    where idarticolo in (select idarticolo from articoli a where a.idproduttore = inIDProduttore);
                    end;

                    -- Procedura di aggiornamento degli archivi sillaro
                    procedure importSistemi (inIDLogElaborazione in integer) is
                    AppID integer;

                    Cursor cOEM is
                    Select *
                    From import_OEM i;

                    Cursor cArticoli is
                    Select *
                    From import_Articoli i;

                    Cursor cSistemi is
                    Select *
                    From import_Sistemi i;

                    Cursor cSistemiArticoli is
                    Select *
                    From import_SistemiArticoli i;

                    Cursor cSchedetecniche is
                    Select *
                    From Import_Schedetecniche i;

                    appOem oem%rowtype;
                    appNominativiOem nominativioem%rowtype;

                    appArticoli articoli%rowtype;

                    appSistemi sistemi%rowtype;
                    appSistemiArticoli sistemiarticoli%rowtype;
                    appSchedeTecniche schedeTecniche%rowtype;

                    i integer;
                    Begin

                    -- OEM
                    Logging.addinformation(inIDLogElaborazione, ' OEM');
                    commit;
                    For rOEM in cOEM Loop
                    appOem.IDOEM := GetIDOEM(rOEM.Nominativo);

                    If appOem.IDOEM is null then
                    appOem.Idoem := dao_sequences.seq_idoem;
                    appOem.Nominativo := InitCap(rOEM.Nominativo);
                    dao_insert.oem(appOem);

                    appNominativiOem.Idnominativioem := dao_sequences.seq_idnominativooem;
                    appNominativiOem.Idoem := appOem.Idoem;
                    appNominativiOem.Nominativo := Upper(rOEM.Nominativo);
                    dao_insert.nominativioem(appNominativiOem);

                    Logging.addinformation(inIDLogElaborazione, ' Inserito l''OEM: ' || rOEM.Nominativo);
                    End If;
                    End Loop;
                    Commit;

                    -- Articoli
                    Logging.addinformation(inIDLogElaborazione, ' Articoli');
                    Commit;
                    i := 0;
                    For rArticolo in cArticoli Loop
                    appArticoli := null;
                    appArticoli.IDArticolo := GetIDArticolo(rArticolo.CodiceProduttore);

                    I := I + 1;
                    Dbms_Output.Put_Line(i);

                    If appArticoli.IDArticolo is null then
                    Logging.addinformation(inIDLogElaborazione, ' Inserire l''articolo: ' || rArticolo.CodiceProduttore
                    || ' - ' || rArticolo.Descrizione);
                    Else
                    If Rarticolo.Immagine Is Not Null Or Rarticolo.Codicioem Is Not Null Then
                    Apparticoli := Dao_Select.Articoli(Apparticoli.Idarticolo);

                    If Rarticolo.Immagine Is Not Null Then
                    Apparticoli.Idimmagine := Getimmagine (Rarticolo.Immagine).Idimmagine;
                    end if;

                    if rArticolo.Codicioem is Not null then
                    appArticoli.Codicioem := rArticolo.CodiciOEM;
                    End If;

                    Dao_Update.Articoli(Apparticoli, Apparticoli);
                    Logging.Addinformation(Inidlogelaborazione, ' Update l''articolo: ' || Rarticolo.Codiceproduttore ||
                    ' - ' || Rarticolo.Descrizione);

                    end if;
                    End If;
                    End Loop;
                    commit;

                    -- Sistemi
                    Logging.addinformation(inIDLogElaborazione, ' Sistemi');
                    commit;
                    For Rsistema In Csistemi Loop
                    Begin
                    Appsistemi := Getsistema(Rsistema.Nominativo);
                    appSistemi.Idoem := GetIDOEM (rSistema.OEM);
                    appSistemi.Cpu := rSistema.CPU;
                    Appsistemi.Bus := Rsistema.Bus;
                    appSistemi.SistemaOperativo := rSistema.SistemaOperativo;
                    appSistemi.Nominativo := rSistema.Nominativo;
                    appSistemi.Descrizione := rSistema.Descrizione;
                    appSistemi.Memoriastandard := rSistema.MemoriaStandard;
                    appSistemi.Memoriamassima := rSistema.MemoriaMassima;
                    appSistemi.Memoriaespansione := rSistema.MemoriaEspansione;
                    appSistemi.Banchi := rSistema.Banchi;
                    appSistemi.Upgrade := rSistema.Upgrade;
                    appSistemi.Commenti := rSistema.Commenti;

                    If appSistemi.IDSistema is null then
                    appSistemi.idSistema := dao_sequences.seq_idsistema;
                    dao_insert.sistemi(appSistemi);

                    Logging.Addinformation(Inidlogelaborazione, ' Inserito il sistema: ' || Rsistema.Nominativo);
                    Else
                    Dao_Update.Sistemi(Appsistemi, Appsistemi);
                    --Logging.Addinformation(Inidlogelaborazione, ' Aggiornato il sistema: ' || Rsistema.Nominativo);
                    End If;
                    Exception
                    When Others Then
                    Logging.Addinformation(Inidlogelaborazione, ' Errore: ' || Sqlerrm);
                    commit;
                    end;
                    End Loop;
                    commit;

                    -- SistemiArticoli
                    Logging.addinformation(inIDLogElaborazione, ' SistemiArticoli');
                    commit;
                    For Rsistemaarticolo In Csistemiarticoli Loop
                    appSistemiArticoli.Idsistema := getsistema (rSistemaArticolo.sistema).idSistema;
                    appSistemiArticoli.Idarticolo := getidArticolo (rSistemaArticolo.articolo);

                    if appSistemiArticoli.Idsistema is null then
                    Logging.addinformation(inIDLogElaborazione, ' Impossibile trovare il sistema: ' ||
                    rSistemaArticolo.Sistema);
                    elsif appSistemiArticoli.Idarticolo is null then
                    Logging.addinformation(inIDLogElaborazione, ' Impossibile trovare l''articolo: ' ||
                    rSistemaArticolo.Articolo);
                    else
                    if Not existssistemaarticolo(appSistemiArticoli.Idsistema, appSistemiArticoli.Idarticolo) then
                    dao_insert.sistemiarticoli(appSistemiArticoli);
                    Logging.addinformation(inIDLogElaborazione, ' Inserito il sistema-articolo: ' ||
                    rSistemaArticolo.Sistema || ' - ' || rSistemaArticolo.Articolo);
                    end if;
                    end if;

                    End Loop;
                    Commit;

                    -- SchedeTecniche
                    Logging.addinformation(inIDLogElaborazione, ' SchedeTecniche');
                    commit;
                    For rSchedetecniche in cSchedetecniche Loop
                    appSchedetecniche.Idarticolo := getidArticolo (rSchedetecniche.articolo);

                    if appSchedetecniche.Idarticolo is not null then
                    appSchedetecniche.Descrizione := rSchedetecniche.descrizione;

                    if Not existsSchedaTecnica(appSchedetecniche.Idarticolo, appSchedetecniche.Descrizione) then
                    appSchedetecniche.Idschedatecnica := dao_sequences.seq_idschedatecnica;
                    appSchedetecniche.Valore := rSchedetecniche.valore;
                    appSchedetecniche.Ordinamento := rSchedetecniche.ordinamento;

                    dao_insert.Schedetecniche(appSchedetecniche);
                    Logging.addinformation(inIDLogElaborazione, ' Inserita la scheda tecnica: ' ||
                    rSchedetecniche.articolo || ' - ' || rSchedetecniche.descrizione);
                    End If;
                    End If;
                    End Loop;
                    commit;

                    end;

                    procedure importArticoli (inIDLogElaborazione in integer, inIDProduttore in integer) is
                    Cursor cArticoli is
                    Select *
                    From import_Articoli i;

                    appArticoli articoli%rowtype;
                    appArticoliFornitori articolifornitori%rowtype;

                    begin
                    For Rarticolo In Carticoli Loop
                    begin
                    appArticoli := getArticoloByCodiceSillaro(rArticolo.codiceSillaro);
                    if appArticoli.Idarticolo is not null then
                    Logging.addinformation(inIDLogElaborazione, ' Codice sillaro già presente: ' ||
                    rArticolo.codiceSillaro);
                    else
                    appArticoli := getArticoloByCodiceProduttore(rArticolo.codiceProduttore);
                    if appArticoli.Idarticolo is not null then
                    Logging.addinformation(inIDLogElaborazione, ' Codice produttore già presente: ' ||
                    rArticolo.codiceProduttore);
                    else
                    appArticoli.Idarticolo := dao_sequences.seq_idarticolo;
                    Apparticoli.Idproduttore := Inidproduttore;
                    Apparticoli.Codicesillaro := Trim(Rarticolo.Codicesillaro);
                    Apparticoli.Codiceproduttore := Trim(Rarticolo.Codiceproduttore);
                    Apparticoli.Codicebarre := Rarticolo.Codicebarre;
                    Apparticoli.Descrizione := Rarticolo.Descrizione;

                    if (rArticolo.tipologia is not null Or Rarticolo.Sottotipologia is not null) then
                    Apparticoli.Idsottotipologia := Getsottotipologia(Inidlogelaborazione, Rarticolo.Tipologia ,
                    Rarticolo.Sottotipologia).Idsottotipologia;
                    Else
                    Apparticoli.Idsottotipologia := GetsottotipologiaByAlias(Inidlogelaborazione,
                    Rarticolo.AliasTipologia , Rarticolo.AliasSottoTipologia).Idsottotipologia;
                    end if;
                    appArticoli.Idstatoarticolo := getstatoarticolo(inIDLogElaborazione,
                    rArticolo.statoarticolo).Idstatoarticolo;
                    appArticoli.Idcategoria09 := getcategoria09(inIDLogElaborazione,
                    rArticolo.categoria09).Idcategoria09;
                    appArticoli.garanzia := rArticolo.garanzia;
                    appArticoli.pezziscatola := rArticolo.pezziscatola;
                    appArticoli.flaglicenza := nvl(rArticolo.flaglicenza, 'N');
                    appArticoli.flagserialnumber := nvl(rArticolo.flagserialnumber, 'N');
                    Apparticoli.Coddurata := Getdurata(Rarticolo.Durata).Coddurata;
                    Apparticoli.Idimmagine := Getimmagine (Rarticolo.Immagine).Idimmagine;
                    dao_insert.articoli(appArticoli);

                    if rArticolo.fornitore is not null and rArticolo.codicefornitore is not null then
                    appArticoliFornitori.Idarticolo := appArticoli.Idarticolo;
                    appArticoliFornitori.Idfornitore := getfornitore(rArticolo.fornitore).idfornitore;
                    appArticoliFornitori.Codicefornitore := rArticolo.codicefornitore;

                    dao_insert.articolifornitori (appArticoliFornitori);
                    end if;

                    Logging.addinformation(inIDLogElaborazione, ' Inserito l''articolo: ' || rArticolo.CodiceProduttore
                    || ' - ' || rArticolo.Descrizione);
                    end if;
                    END IF;
                    EXCEPTION
                    WHEN OTHERS THEN
                    Logging.adderror(inIDLogElaborazione, ' Errore sull''articolo: ' || rArticolo.CodiceProduttore || '
                    - ' || rArticolo.Descrizione || sqlerrm);
                    end;
                    End Loop;
                    end;

                    Procedure Updatearticoli (Inidproduttore In Integer) Is
                    Idlogelaborazione Integer := Dao_Sequences.Seq_Idlogelaborazione;
                    Begin
                    Logging.Startlog(Idlogelaborazione, 'Update articoli');
                    Updatearticoli (Idlogelaborazione, Inidproduttore);
                    Logging.Endlog(Idlogelaborazione);
                    End;


                    procedure updateArticoli (inIDLogElaborazione in integer, inIDProduttore in integer) is
                    Cursor cArticoli is
                    Select *
                    From import_Articoli i;

                    appArticoli articoli%rowtype;

                    appCms cms%rowtype;

                    begin
                    For rArticolo in cArticoli Loop
                    appArticoli := getArticoloByCodiceProduttore(rArticolo.codiceProduttore);
                    if appArticoli.Idarticolo is not null then
                    if appArticoli.idImmagine is null then
                    apparticoli.idImmagine := getImmagine (rArticolo.immagine).idImmagine;
                    end if;

                    if appArticoli.idCms is null then
                    appCms.idCms := dao_sequences.seq_idcms;
                    appCms.idtipoCms := 3;
                    appCms.descrizione := 'Scheda Drako per: ' || rArticolo.codiceproduttore;
                    appCms.corpo := rArticolo.cms;

                    dao_insert.cms (appCms);
                    appArticoli.idcms := appCms.idCms;
                    end if;

                    dao_update.articoli(apparticoli, apparticoli);
                    Logging.addinformation(inIDLogElaborazione, ' Aggiornato articolo: ' || rArticolo.codiceProduttore);
                    End If;
                    End Loop;
                    end;

                    Procedure ObsolescenzaArticoli (Inidlogelaborazione In Integer) is
                    Cursor cArticoli is
                    Select *
                    From import_Articoli i;

                    appArticoli articoli%rowtype;

                    begin
                    For rArticolo in cArticoli Loop
                    appArticoli := getArticoloByCodiceProduttore(rArticolo.codiceProduttore);
                    if appArticoli.Idarticolo is not null then
                    appArticoli.idstatoArticolo := 5;
                    dao_update.articoli(apparticoli, apparticoli);

                    pck_prezzi.Insertprezzovendita (appArticoli.Idarticolo, trunc(sysdate), null, null, null);

                    Logging.addinformation(inIDLogElaborazione, ' Aggiornato articolo: ' || rArticolo.codiceProduttore);
                    End If;
                    End Loop;
                    end;


                    procedure importArticoliCliente (inIDLogElaborazione in integer, inIDCliente in integer) is
                    Cursor cArticoliCliente is
                    Select *
                    From import_ArticoliClienti i;

                    appArticoli articoli%rowtype;
                    appArticoliClienti ArticoliClienti%rowtype;

                    begin
                    For rArticolo in cArticoliCliente Loop
                    begin
                    if rArticolo.codiceSillaro is not null then
                    appArticoli := getArticoloByCodiceSillaro(rArticolo.codiceSillaro);

                    if appArticoli.Idarticolo is null then
                    Logging.addinformation(inIDLogElaborazione, ' Codice sillaro non trovato: ' ||
                    rArticolo.codiceSillaro);
                    end if;
                    elsif rArticolo.codiceProduttore is not null then
                    appArticoli := getArticoloByCodiceProduttore(rArticolo.codiceProduttore);
                    if appArticoli.Idarticolo is null then
                    Logging.addinformation(inIDLogElaborazione, ' Codice produttore non trovato: ' ||
                    rArticolo.codiceProduttore);
                    end if;
                    end if;

                    if appArticoli.Idarticolo is not null then
                    appArticoliClienti.Idarticolo := appArticoli.Idarticolo;
                    appArticoliClienti.Idcliente:= inIDCliente;
                    appArticoliClienti.Codicecliente := rArticolo.Codicecliente;

                    dao_insert.articoliclienti(appArticoliClienti);
                    Logging.addinformation(inIDLogElaborazione, ' Censito nuovo codice: ' || rArticolo.codicecliente);
                    end if;
                    exception
                    when others then
                    Logging.Addwarning(inIDLogElaborazione, ' Errore caricamento codice: ' || rArticolo.codiceSillaro ||
                    ' ' || rArticolo.codicecliente || ' ' || sqlerrm);
                    end;
                    End Loop;

                    end;

                    Procedure CrawlClear Is
                    Begin
                    Execute Immediate 'truncate table CRAWLED_OEM';
                    Execute Immediate 'truncate table CRAWLED_SISTEMI';
                    Execute Immediate 'truncate table CRAWLED_SISTEMIARTICOLO';
                    End;


                    procedure CrawlToImport (inIDLogElaborazione in integer) is
                    procedure popola (inTipo in varchar2) is
                    begin
                    insert into Import_Oem
                    (NOMINATIVO)
                    Select OEM
                    from Crawled_Oem
                    where tipo = inTipo;

                    insert into Import_Sistemi
                    (nominativo, OEM)
                    Select OEM || ' - ' || Sistema, OEM
                    from Crawled_Sistemi
                    where tipo = inTipo;

                    insert into Import_Sistemiarticoli
                    (SISTEMA, ARTICOLO, SOTTOTIPOLOGIA)
                    Select OEM || ' - ' || Sistema, CODICEPRODUTTORE, 'Batterie ed alimentatori compatibili per Notebook
                    e Netbook'
                    from Crawled_Sistemiarticolo
                    where tipo = inTipo;
                    end;

                    Begin
                    Logging.addinformation(inIDLogElaborazione, ' Clear');
                    clear;

                    -- FEDCO
                    Logging.addinformation(inIDLogElaborazione, ' Import FEDCO');
                    Popola (Costanti.Crawl_Fedco);

                    -- DR.BATTERY
                    Logging.Addinformation(Inidlogelaborazione, ' Import DR.BATTERY');
                    popola (Costanti.CRAWL_DRBATTERY);
                    end;
                    end;

