PACKAGE PCK_LICENZE AS

                    function getLicenze(inIdPacchetto in integer) return integer;
                    function getLicenzeDisponibili return integer;
                    function creaPacchetto (inDescrizione in varchar2, inNumeroLicenze in integer) return integer;

                    END;

Package.SourceBody:
PACKAGE body PCK_LICENZE AS

                    function getLicenze(inIdPacchetto in integer) return integer is
                    app integer;
                    begin
                    Select count(*)
                    into app
                    from LICENZE
                    where idpacchetto = inIdPacchetto;

                    return app;
                    end;

                    function getLicenzeDisponibili return integer is
                    app integer;
                    begin
                    Select count(*)
                    into app
                    from LICENZE
                    where idpacchetto is null;

                    return app;
                    end;

                    function creaPacchetto (inDescrizione in varchar2, inNumeroLicenze in integer) return integer is
                    app integer;
                    appPacchetto pacchetti%rowtype;
                    begin
                    -- Verifico se ci sono licenze disponibili
                    app := getLicenzeDisponibili;
                    if app < inNumeroLicenze then
                    raise_application_error (-20000, 'Sono disponibili solo: ' || app || ' licenze');
                    end if;

                    -- Creo il pacchetto
                    appPacchetto.idPacchetto := DAO_SEQUENCES.SEQ_IDPACCHETTO;
                    appPacchetto.Descrizione := inDescrizione;
                    appPacchetto.data := sysdate;
                    DAO_INSERT.PACCHETTI(appPacchetto);

                    -- Associo le licenze
                    update LICENZE
                    set idPacchetto = appPacchetto.idPacchetto
                    where idPacchetto is null And
                    rownum <= inNumeroLicenze;

                    return appPacchetto.idPacchetto;
                    end;

                    END;

Package.SourceSpec:
package pck_mailingList is

                    procedure populateMailingList;

                    end;

Package.SourceBody:
package body pck_mailingList is

                    function existMail (inMail in varchar2) return boolean is
                    result integer;
                    begin
                    Select count(*)
                    into result
                    from temp_mailingList
                    where email = lower(inMail);

                    return (result = 1);
                    end;

                    procedure populateMailingList is
                    Cursor cUtentiSito is
                    Select *
                    from SITO_UTENTI
                    where dataModificaPassword is not null
                    order by idutente;

                    Cursor Cnominativi Is
                    Select n.*
                    from Nominativi n, clienti c, SITO_UTENTI su
                    where c.idcliente = su.idcliente And
                    c.idcliente = n.idcliente And
                    n.postaelettronica is not null and
                    n.flagescludimail = 'N' And
                    dataModificaPassword is not null
                    order by idnominativo;


                    Cursor cProspect Is
                    Select mp.*
                    From Mail_Prospect Mp
                    where Mp.Flagescludimail = 'N';

                    appMailingList temp_MailingList%rowtype;

                    begin
                    -- Recupero le mail dagli utenti sito
                    For rUtentiSito in cUtentiSito loop
                    appMailingList.Email := trim(lower(rUtentiSito.email));
                    appMailingList.Idcliente := rUtentiSito.Idcliente;
                    appMailingList.Idnominativo := null;
                    appMailingList.Idutentesito := rUtentiSito.Idutente;
                    appMailingList.Idutentefrom := null;
                    appMailingList.Provenienza := 'sito';

                    if not existMail(appMailingList.Email) then
                    dao_insert.temp_mailinglist(appMailingList);
                    end if;
                    End Loop;

                    -- Recupero le mail dai nominativi
                    For rNominativi in cNominativi loop
                    appMailingList.Email := trim(lower(rNominativi.postaelettronica));
                    appMailingList.Idcliente := rNominativi.Idcliente;
                    appMailingList.Idnominativo := rNominativi.Idnominativo;
                    appMailingList.Idutentesito := null;
                    appMailingList.Idutentefrom := null;
                    appMailingList.Provenienza := 'nominativi';

                    if not existMail(appMailingList.Email) then
                    dao_insert.temp_mailinglist(appMailingList);
                    end if;
                    End Loop;

                    -- Recupero le mail dai prospect
                    For rProspect in cProspect loop
                    Appmailinglist.Email := Trim(Lower(rProspect.Email));
                    Appmailinglist.Idcliente := Null;
                    appMailingList.Idnominativo := null;
                    appMailingList.Idutentesito := null;
                    Appmailinglist.Idutentefrom := Null;
                    appMailingList.Provenienza := 'prospect';

                    Dbms_Output.put_line ('pppp: ' || Appmailinglist.Email);

                    If Not Existmail(Appmailinglist.Email) Then
                    Dbms_Output.put_line ('ddddd');
                    Dao_Insert.Temp_Mailinglist(Appmailinglist);
                    end if;
                    End Loop;

                    end;
                    end;

Package.SourceSpec:
Package Pck_Offerte Is

                    Function GetImportoNetto(inIdoffertaCliente Integer) Return Float;
                    Function GetIVA(Inidoffertacliente Integer) Return Float;
                    Function GetImporto(inIdoffertaCliente Integer) Return Float;

                    End;

Package.SourceBody:
package body pck_Offerte is

                    Function GetimportoNetto(Inidoffertacliente Integer) Return Float Is
                    result float;
                    Begin
                    Select Sum(Ro.Quantita * Ro.Prezzo) ImportoNetto
                    Into result
                    From Righeofferteclienti Ro
                    Where Ro.Idoffertacliente = Inidoffertacliente;

                    return result;
                    end;

                    Function GetIVA(Inidoffertacliente Integer) Return Float Is
                    result float;
                    Begin
                    Select Sum(round(Ro.Quantita * Ro.Prezzo * A.Aliquota / 100, 2)) IVA
                    Into Result
                    From Righeofferteclienti Ro, Dec_Aliquote A
                    Where Ro.Codaliquota = A.Codaliquota And
                    Ro.Idoffertacliente = Inidoffertacliente;

                    Return Result;
                    end;

                    Function GetImporto(Inidoffertacliente Integer) Return Float Is
                    result float;
                    Begin
                    Select Sum(Ro.Quantita * Ro.Prezzo + round(Ro.Quantita * Ro.Prezzo * A.Aliquota / 100, 2)) Importo
                    Into result
                    From Righeofferteclienti Ro, Dec_Aliquote A
                    Where Ro.Codaliquota = A.Codaliquota And
                    Ro.Idoffertacliente = Inidoffertacliente;

                    Return Result;
                    end;
                    end;

