Package.SourceSpec:
package pck_ordinifornitori is

                    function getPrezzo (inIdArticolo in integer, inIdFornitore in integer, inQuantita in integer, inData
                    in date) return float;
                    function getRiferimentoSpecial (inIdArticolo in integer, inIdFornitore in integer, inQuantita in
                    integer, inData in date) return varchar2;
                    function getNote (inIdArticolo in integer, inIdFornitore in integer, inQuantita in integer, inData
                    in date) return varchar2;

                    function getTotaleOrdine (inIDOrdineFornitore in integer) return float;

                    function creaOrdiniLicenze return integer;
                    function getIdOrdineCliente (inidordinefornitore in integer) return integer;
                    procedure setIdOrdineCliente (inidordinefornitore in integer, inIdOrdineCliente in integer);
                    end;

Package.SourceBody:
package body pck_ordinifornitori is
                    cursor cSpecial (inIdArticolo in integer, inIdFornitore in integer, inData in date) is
                    Select rs.quantita,
                    rs.riferimentospecial,
                    rs.specialacquisto
                    From Righespecial rs, Special s
                    where s.idspecial = rs.idspecial And
                    rs.idarticolo = inIdArticolo And
                    rs.idfornitore = inIdFornitore And
                    nvl(s.datafinevalidita, costanti.max_data) >= inData And
                    specialacquisto is not null
                    Order by rs.specialacquisto desc;

                    function getPrezzo (inIdArticolo in integer, inIdFornitore in integer, inQuantita in integer, inData
                    in date) return float is
                    appPrezzoAcquisto float;

                    begin
                    -- Prezzo Acquisto standard
                    appPrezzoAcquisto := pck_prezzi.getprezzoacquisto(inIdArticolo, inIdFornitore, inData);

                    -- Verifico la presenza di special
                    For rSpecial in cSpecial(inIdArticolo, inIdFornitore, inData) Loop
                    if nvl(rSpecial.quantita, 0) <= inQuantita then
                    if (appPrezzoAcquisto > rSpecial.specialacquisto) then
                    return rSpecial.specialacquisto;
                    end if;
                    end if;
                    End Loop;

                    return appPrezzoAcquisto;
                    end;

                    function getRiferimentoSpecial (inIdArticolo in integer, inIdFornitore in integer, inQuantita in
                    integer, inData in date) return varchar2 is
                    appPrezzoSpecial float := pck_prezzi.getprezzoacquisto(inIdArticolo, inIdFornitore, inData);

                    begin
                    -- Verifico la presenza di special
                    For rSpecial in cSpecial(inIdArticolo, inIdFornitore, inData) Loop
                    if nvl(rSpecial.quantita, 0) <= inQuantita then
                    if (appPrezzoSpecial > rSpecial.specialacquisto) then
                    return rSpecial.riferimentospecial;
                    end if;
                    end if;
                    End Loop;

                    return null;
                    end;

                    function getNote (inIdArticolo in integer, inIdFornitore in integer, inQuantita in integer, inData
                    in date) return varchar2 is
                    appPrezzoSpecial float := pck_prezzi.getprezzoacquisto(inIdArticolo, inIdFornitore, inData);
                    begin
                    -- Verifico la presenza di special
                    dbms_output.put_line ('aaaa');

                    For rSpecial in cSpecial(inIdArticolo, inIdFornitore, inData) Loop
                    dbms_output.put_line ('ooo');

                    if nvl(rSpecial.quantita, 0) <= inQuantita then
                    exit;
                    else
                    return 'Attenzione: Special ' || rSpecial.riferimentospecial || ' valido per ' || rSpecial.quantita
                    || ' pz';
                    end if;
                    End Loop;

                    return null;
                    end;

                    function getTotaleOrdine (inIDOrdineFornitore in integer) return float is
                    result float;
                    Begin
                    Select sum (QUANTITA*PREZZO) importo
                    into result
                    From righeOrdinifornitori roc
                    Where roc.idOrdineFornitore = inIDOrdineFornitore;

                    return result;
                    End;

                    function creaOrdiniLicenze return integer is
                    Cursor cOrdiniClienti is
                    select oc.*
                    from ordiniclienti oc
                    where oc.flaglicenza = 'S' And
                    oc.flaglicenzaordinata = 'N';

                    Cursor cRigheOrdiniClienti (inIdOrdineCliente in integer, inIdFornitore in integer) is
                    select roc.*
                    from articolifornitori af, articoli a, righeordiniclienti roc
                    where roc.idarticolo = a.idarticolo And
                    a.idarticolo = af.idarticolo And
                    af.idfornitore = inIdFornitore And
                    a.flaglicenza = 'S' And
                    roc.flaglicenzaordinata = 'N' And
                    roc.idordinecliente = inIdOrdineCliente;

                    result integer := null;

                    appIdFornitore integer;
                    appIdProduttore integer;
                    appCliente clienti%rowtype;

                    appOrdinifornitori ordinifornitori%rowtype;
                    appRigheOrdinifornitori righeordinifornitori%rowtype;
                    Begin
                    For rOrdiniClienti in cOrdiniClienti Loop
                    Select min(f.idfornitore), min(IdProduttore)
                    Into appIdFornitore, appIdProduttore
                    From fornitori f, articolifornitori af, articoli a, righeordiniclienti roc
                    Where a.idarticolo = roc.idarticolo And
                    a.idarticolo = af.idarticolo And
                    af.idfornitore = f.idfornitore And
                    a.flaglicenza = 'S' And
                    roc.flaglicenzaordinata = 'N' And
                    roc.idordinecliente = rOrdiniClienti.idordinecliente And
                    f.FLAGATTIVO = 'S';

                    if appIdFornitore is null then
                    rOrdiniClienti.flaglicenzaordinata := 'S';
                    dao_update.ordiniclienti(rOrdiniClienti, rOrdiniClienti);
                    else
                    appCliente := dao_select.clienti(rOrdiniClienti.idCliente);

                    -- Testata ordine
                    appOrdinifornitori.Idordinefornitore := dao_sequences.seq_idordinefornitore;
                    appOrdinifornitori.Idfornitore := appIdFornitore;
                    appOrdinifornitori.Data := trunc(sysdate);
                    appOrdinifornitori.Numeroordinefornitore := to_char(sysdate, 'Y') ||
                    lpad(appOrdinifornitori.Idordinefornitore, 5, '0');
                    appOrdinifornitori.Idutente := null;
                    appOrdinifornitori.Destinatario := null;
                    appOrdinifornitori.Commento := rOrdiniClienti.licenza;

                    if appIdProduttore = Costanti.Prod_Kaspersky then
                    appOrdinifornitori.Rivenditore := appCliente.Ragionesociale || ' - P.IVA: ' || appCliente.PARTITAIVA
                    || ' - Indirizzo: ' || appCliente.INDIRIZZO || ' ' || appCliente.CAP || ' - ' ||
                    appCliente.LOCALITA;
                    end if;

                    appOrdinifornitori.Noteinterne := appCliente.Ragionesociale;
                    Appordinifornitori.Flaginviato := 'N';
                    Appordinifornitori.Flagtrasferitogamma := 'N';
                    dao_insert.ordinifornitori (appOrdinifornitori);

                    For rRigheOrdiniClienti in cRigheOrdiniClienti (rOrdiniClienti.idOrdineCliente, appIdFornitore) Loop
                    appRigheOrdinifornitori.Idrigaordinefornitore := dao_sequences.seq_idrigaordinefornitore;
                    appRigheOrdinifornitori.Idordinefornitore := appOrdinifornitori.Idordinefornitore;
                    appRigheOrdinifornitori.Idarticolo := rRigheOrdiniClienti.Idarticolo;
                    appRigheOrdinifornitori.Note := null;
                    appRigheOrdinifornitori.Quantita := rRigheOrdiniClienti.QUANTITAORDINATA;
                    appRigheOrdinifornitori.Riferimentospecial := null;
                    appRigheOrdinifornitori.Prezzo := pck_prezzi.getprezzoacquisto(appRigheOrdinifornitori.Idarticolo,
                    appOrdinifornitori.Idfornitore, appOrdinifornitori.Data);

                    dao_insert.righeordinifornitori(appRigheOrdinifornitori);

                    rRigheOrdiniClienti.flaglicenzaordinata := 'S';
                    dao_update.RigheOrdiniClienti (rRigheOrdiniClienti, rRigheOrdiniClienti);
                    End Loop;

                    rOrdiniClienti.Idordinefornitore := appOrdinifornitori.Idordinefornitore;
                    Dao_update.OrdiniClienti(rOrdiniClienti, rOrdiniClienti);

                    result := appOrdinifornitori.Idordinefornitore;
                    exit;

                    End if;
                    End Loop;

                    return result;
                    End;

                    function getIdOrdineCliente (inidordinefornitore in integer) return integer as
                    result integer;
                    begin
                    select min(IdOrdineCliente)
                    into result
                    from ordiniclienti
                    where idordinefornitore = inidordinefornitore;

                    return result;
                    end;

                    procedure setIdOrdineCliente (inidordinefornitore in integer, inIdOrdineCliente in integer) as
                    begin
                    -- Annullo una eventuale vecchia associazione
                    update ordiniclienti
                    set idordinefornitore = null
                    where idordinefornitore = inidordinefornitore;

                    -- Attivo la nuova associazione
                    update ordiniclienti
                    set idordinefornitore = inidordinefornitore
                    where idordinecliente = inIdOrdineCliente;
                    end;
                    end;

