Package.SourceBody:
package body pck_prezzi is
  cursor cSpecial (inIdArticolo in integer, inIdCliente in integer, inData in date) is
    Select rs.quantita,
           rs.riferimentospecial,
           rs.specialvendita
    From Righespecial rs, Special s
    where s.idspecial = rs.idspecial And
          rs.idarticolo = inIdArticolo And
          s.idcliente = inIdCliente And
          nvl(s.datafinevalidita, costanti.max_data) >= inData And
          specialvendita is not null
    Order by rs.specialacquisto desc;

  -- Gestisce l'inserimento di un prezzo di vendita
  procedure InsertPrezzoVendita (InIDArticolo In Integer,
                                 InDataInizioValidita in date,
                                 InDataFineValidita in date,
                                 InPrezzo in float,
                                 InPrezzoNetto in float) is

      AppPrezziVendita PrezziVendita%rowtype;

      Cursor Cprezzi Is
         Select *
         From PrezziVendita
         Where Idarticolo = Inidarticolo And
               Not (Datainiziovalidita > nvl(InDataFineValidita, Costanti.MAX_DATA) Or nvl(Datafinevalidita, Costanti.MAX_DATA) < InDataInizioValidita);

   Begin
      For rPrezzi in cPrezzi Loop
        If Rprezzi.Datainiziovalidita >= Indatainiziovalidita And Nvl(Rprezzi.Datafinevalidita, Costanti.Max_Data) <= Nvl(Indatafinevalidita, Costanti.Max_Data) Then
          Dao_Delete.Prezzivendita (Rprezzi);
        Elsif Rprezzi.Datainiziovalidita >= Indatainiziovalidita Then
          Appprezzivendita := Rprezzi;
          Appprezzivendita.Datainiziovalidita :=  Nvl(Indatafinevalidita, Costanti.Max_Data) + 1;

          Dao_Update.Prezzivendita (Rprezzi, Appprezzivendita);
        Elsif  Nvl(Rprezzi.Datafinevalidita, Costanti.Max_Data) <= Nvl(Indatafinevalidita, Costanti.Max_Data) Then
          Appprezzivendita := Rprezzi;
          Appprezzivendita.DatafineValidita := Indatainiziovalidita-1;

          Dao_Update.Prezzivendita (Rprezzi, Appprezzivendita);
        else
          Dao_Delete.Prezzivendita (Rprezzi);

          Appprezzivendita := Rprezzi;
          Appprezzivendita.Datainiziovalidita :=  Nvl(Indatafinevalidita, Costanti.Max_Data) + 1;
          Dao_Insert.Prezzivendita (Appprezzivendita);

          Appprezzivendita := Rprezzi;
          Appprezzivendita.DatafineValidita := Indatainiziovalidita-1;
          Dao_Insert.Prezzivendita (Appprezzivendita);
        end if;

      End Loop;

      -- Aggiungo il nuovo prezzo
      Appprezzivendita.Idarticolo := Inidarticolo;
      Appprezzivendita.Datainiziovalidita := Indatainiziovalidita;
      Appprezzivendita.Datafinevalidita := Indatafinevalidita;

      If Inprezzo = 0 Then
        Appprezzivendita.Prezzo := null;
      else
        Appprezzivendita.Prezzo := Round(Inprezzo, 2);
      end if;

      If InPrezzoNetto = 0 Then
        Appprezzivendita.Prezzonetto := null;
      else
        Appprezzivendita.Prezzonetto := Round(InPrezzoNetto, 2);
      end if;

      Dao_Insert.Prezzivendita (Appprezzivendita);
    end;

  Procedure Insertprezzoacquisto (Inidarticolo In Integer, Inidfornitore In Integer,
                                  InDataInizioValidita in date, InDataFineValidita in date, InPrezzo in float,
                                  InIDValuta in integer) is

      AppprezziAcquisto PrezziAcquisto%Rowtype;

      Cursor cPrezzi is
         Select *
         From PrezziAcquisto
         Where IDArticolo = InIDArticolo And
               IDFornitore = InIDFornitore And
               Not (Datainiziovalidita > nvl(InDataFineValidita, Costanti.MAX_DATA) Or nvl(Datafinevalidita, Costanti.MAX_DATA) < InDataInizioValidita);

  Begin
    For rPrezzi in cPrezzi Loop
        If Rprezzi.Datainiziovalidita >= Indatainiziovalidita And Nvl(Rprezzi.Datafinevalidita, Costanti.Max_Data) <= Nvl(Indatafinevalidita, Costanti.Max_Data) Then
          Dao_Delete.PrezziAcquisto (Rprezzi);
        Elsif Rprezzi.Datainiziovalidita >= Indatainiziovalidita Then
          AppprezziAcquisto := Rprezzi;
          AppprezziAcquisto.Datainiziovalidita :=  Nvl(Indatafinevalidita, Costanti.Max_Data) + 1;

          Dao_Update.PrezziAcquisto (Rprezzi, AppprezziAcquisto);
        Elsif  Nvl(Rprezzi.Datafinevalidita, Costanti.Max_Data) <= Nvl(Indatafinevalidita, Costanti.Max_Data) Then
          AppprezziAcquisto := Rprezzi;
          AppprezziAcquisto.DatafineValidita := Indatainiziovalidita-1;

          Dao_Update.Prezziacquisto (Rprezzi, Appprezziacquisto);
        Else
          Dao_Delete.Prezziacquisto (Rprezzi);

          AppprezziAcquisto := Rprezzi;
          Appprezziacquisto.Datainiziovalidita :=  Nvl(Indatafinevalidita, Costanti.Max_Data) + 1;
          Dao_Insert.Prezziacquisto (Appprezziacquisto);

          AppprezziAcquisto := Rprezzi;
          Appprezziacquisto.Datafinevalidita := Indatainiziovalidita-1;
          Dao_Insert.Prezziacquisto (Appprezziacquisto);
        end if;

      End Loop;

      -- Aggiungo il nuovo prezzo
      Appprezziacquisto.Idarticolo := Inidarticolo;
      AppprezziAcquisto.Idfornitore := Inidfornitore;
      AppprezziAcquisto.Datainiziovalidita := Indatainiziovalidita;
      Appprezziacquisto.Datafinevalidita := Indatafinevalidita;
      Appprezziacquisto.Prezzo := round(Inprezzo, 2);
      Appprezziacquisto.Idvaluta := InIdvaluta;

      Dao_Insert.PrezziAcquisto (AppprezziAcquisto);
  end;


  -- Ritorna il valore dello sconto da applicare ad un articolo
  function getSconto (InIDArticolo In Integer, inIDSconto in Integer) return float is
       AppSconto float;

     begin
       Select max(Sconto)
       Into AppSconto
       from Articoli a, ScontiCategorie09 sc
       Where a.IDCategoria09 = sc.IDCategoria09 And
             a.IDArticolo = InIDArticolo And
             sc.IDsconto = InIDSconto;

       if AppSconto is null then
          Select max(Sconto)
          Into AppSconto
          from Sconti s
          Where s.IDsconto = InIDSconto;
       end if;

       return AppSconto;
     end;

  function selectPrezzoVendita (InIDArticolo in Integer, InData in Date) return Prezzivendita%rowtype is
      Result Prezzivendita%rowtype;

   begin
      Select *
      Into Result
      From PrezziVendita
      Where Idarticolo = Inidarticolo And
            InData between DataInizioValidita And nvl (DataFineValidita, to_date('31/12/9999', 'dd/mm/yyyy'));

      return Result;

   exception
      when others then
         return null;
   end;

  function getPrezzoListino(inIDArticolo in integer,  inData in date) return float is
      AppPrezzoVendita Prezzivendita%rowtype;
      AppSconto float;
    begin
      AppPrezzoVendita := selectPrezzoVendita(inIDArticolo, inData);

      return AppPrezzoVendita.Prezzo;
    end;

  function getPrezzoListino(inIDArticolo in integer) return float is
    begin
      return getPrezzoListino (inIDArticolo, trunc(sysdate));
    end;

  function getPrezzoNetto(inIDArticolo in integer, inData in date) return float is
      AppPrezzoVendita Prezzivendita%rowtype;
      AppSconto float;
    begin
      AppPrezzoVendita := selectPrezzoVendita(inIDArticolo, inData);

      return AppPrezzoVendita.Prezzonetto;
    end;

  function getPrezzoNetto(inIDArticolo in integer) return float is
    begin
      return getPrezzoNetto (inIDArticolo, trunc(sysdate));
    end;

  function getPrezzoVendita(inIDArticolo in integer, inIDSconto in integer, inData in date) return float is
      Appprezzovendita Prezzivendita%Rowtype;
      AppSconto float;
      AppSiae float;
      AppArticolo articoli%rowtype;
    begin
      AppPrezzoVendita := selectPrezzoVendita(inIDArticolo, inData);

      If AppPrezzoVendita.Prezzonetto is not null then
        -- Prezzo netto
        return AppPrezzoVendita.Prezzonetto;
      Elsif inIDSconto is null then
        -- Prezzo di listino
        return AppPrezzoVendita.Prezzo;
      Else
        -- Prezzo scontato
        AppSconto := getSconto(inIDArticolo, inIDSconto);

        AppArticolo := dao_select.articoli(inIDArticolo);
        AppSiae := nvl(getCostoSiae(AppArticolo.SIAE_CodCategoria, AppArticolo.SIAE_capienza, inData), 0);

        return round((100-AppSconto) * (AppPrezzoVendita.Prezzo - AppSiae) / 100 + AppSiae, 2);
      End if;
    end;

  function getPrezzoVendita (inIDArticolo in integer, inIDSconto in integer) return float is
    begin
      return getPrezzoVendita(inIDArticolo, inIDSconto, trunc(sysdate));
    end;

  function getPrezzoVendita (inIDArticolo in integer) return float is
    begin
      return getPrezzoVendita(inIDArticolo, null, trunc(sysdate));
    end;

  Function Bestprezzovendita (Inidcliente In Integer, Inidarticolo In Integer, Inquantita In Integer) Return Float Is
      appCliente clienti%rowtype;
      prezzoVendita float;
    begin
      if inIDCliente is null then
        return getprezzolistino(inidarticolo, trunc(sysdate));
      end if;

      appCliente := dao_select.clienti(inIDCliente);

      -- Prezzo vendita
      prezzoVendita := getprezzovendita(inIDArticolo, appCliente.Idsconto, trunc(sysdate));

      -- Prezzo special
      For rSpecial in cSpecial (inIDArticolo, inIDCliente, trunc(sysdate)) Loop
        if rSpecial.quantita <= inQuantita And rSpecial.specialVendita < prezzoVendita then
          prezzoVendita := rSpecial.specialVendita;
        end if;
      End Loop;

      return prezzoVendita;
    end;

  function bestPrezzoVenditaNote (inIDCliente in integer, inIDArticolo in integer, inQuantita in integer) return varchar2 is
      appCliente clienti%rowtype;
      prezzoVendita float;
      note varchar2 (100);
    begin
      if inIDCliente is null then
        return null;
      end if;

      appCliente := dao_select.clienti(inIDCliente);

      -- Prezzo vendita
      prezzoVendita := getprezzovendita(inIDArticolo, appCliente.Idsconto, trunc(sysdate));

      -- Prezzo special
      For rSpecial in cSpecial (inIDArticolo, inIDCliente, trunc(sysdate)) Loop
        if rSpecial.specialVendita < prezzoVendita then
          if rSpecial.quantita <= inQuantita then
            prezzoVendita := rSpecial.specialVendita;
            note := 'Applicato prezzo special ' || rSpecial.riferimentospecial;
          else
            note := 'Attenzione! Special attivo per ' || rSpecial.quantita || ' pz a ' || rSpecial.specialVendita || ' Euro';
          end if;
        end if;
      End Loop;

      return note;
    end;

  function getPrezzoAcquisto (InIDArticolo in Integer, InIDFornitore in Integer, InData in Date) return float is
      Result float;
    begin
      Select min(Prezzo)
      Into Result
      From PrezziAcquisto
      Where IDArticolo = InIDArticolo And
            IDFornitore = InIDFornitore And
            InData between DataInizioValidita And nvl (DataFineValidita, to_date('31/12/9999', 'dd/mm/yyyy'));

      return Result;
    end;

  function getPrezzoAcquisto (InIDArticolo in Integer, InData in Date) return float is
      Result float;
    begin
      Select min(Prezzo)
      Into Result
      From PrezziAcquisto
      Where IDArticolo = InIDArticolo And
            InData between DataInizioValidita And nvl (DataFineValidita, to_date('31/12/9999', 'dd/mm/yyyy'));

      return Result;
    end;

  function getPrezzoAcquisto (InIDArticolo in Integer) return float is
      Result float;
    begin
      Return getPrezzoAcquisto(InIDArticolo, trunc(sysdate));
    end;

  function getPrezzoAcquistoEuro (InIDArticolo in Integer, InData in Date) return float is
      Result float;
    begin
      Select min(Prezzo / v.cambio)
      Into Result
      From valute v, fornitori f, PrezziAcquisto pa
      Where pa.idfornitore = f.idfornitore And
            f.idvaluta = v.idvaluta And
            IDArticolo = InIDArticolo And
            InData between DataInizioValidita And nvl (DataFineValidita, to_date('31/12/9999', 'dd/mm/yyyy'));

      return Result;
    end;

  function getCostoSiae (inCodCategoria in varchar2, inCapienza in float, inData in date) return float is
      appCategoriaSiae siae_dec_categorie%rowtype;
      appTariffaSiae siae_tariffe%rowtype;

    begin
      if inCodCategoria is null Or inCapienza is null then
        return null;
      end if;

      appCategoriaSiae := dao_select.siae_dec_categorie(inCodCategoria);

      Select *
      Into appTariffaSiae
      from siae_tariffe t
      where t.codcategoria = inCodCategoria And
            inCapienza between t.capienzada And nvl(t.capienzaa, costanti.max_capienza);

      if inData < to_date('01/01/2012', 'dd/mm/yyyy') then
        return least (appTariffaSiae.Importo * inCapienza, appCategoriaSiae.Max2010);
      else
        return least (appTariffaSiae.Importo * inCapienza, appCategoriaSiae.Max2012);
      end if;

    end;

  function getCostoSiae (inCodCategoria in varchar2, inCapienza in float) return float is
    begin
      return getCostoSiae(inCodCategoria, inCapienza, sysdate);
    end;

end;

