Package Pck_Produttori Is

                    Function Getarticoliattivi (Inidproduttore In Integer) Return Integer;
                    Function Getcodiciamagazzino (Inidproduttore In Integer) Return Integer;
                    Function Getgiacenzaamagazzino (Inidproduttore In Integer) Return Integer;
                    Function getGiacenzaAMagazzinoNonShip (inIdProduttore in integer) return integer;

                    procedure codificaArticoli;
                    procedure classifica;

                    end;

Package.SourceBody:
package body pck_produttori is

                    function getArticoliAttivi (inIdProduttore in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    into result
                    from Articoli a
                    where a.idproduttore = inIdProduttore And
                    a.idstatoarticolo = costanti.stat_art_shipping;

                    If Result = 0 Then
                    Return Null;
                    Else
                    Return Result;
                    End If;

                    Return Result;
                    end;

                    function getCodiciAMagazzino (inIdProduttore in integer) return integer is
                    result integer;

                    begin
                    select count(*)
                    Into Result
                    From Giacenze G, Articoli A
                    Where A.Idproduttore = Inidproduttore And
                    --A.Idstatoarticolo = Costanti.Stat_Art_Shipping And
                    a.Idarticolo = g.Idarticolo And
                    g.giacenza > 0;

                    If Result = 0 Then
                    Return Null;
                    Else
                    Return Result;
                    End If;

                    end;

                    function getGiacenzaAMagazzino (inIdProduttore in integer) return integer is
                    result integer;

                    Begin
                    select sum(giacenza)
                    Into Result
                    From Giacenze G, Articoli A
                    Where A.Idproduttore = Inidproduttore And
                    --A.Idstatoarticolo = Costanti.Stat_Art_Shipping And
                    A.Idarticolo = G.Idarticolo And
                    g.giacenza > 0;

                    Return Result;
                    end;

                    Function getGiacenzaAMagazzinoNonShip (inIdProduttore in integer) return integer is
                    result integer;

                    Begin
                    select sum(giacenza)
                    Into Result
                    From Giacenze G, Articoli A
                    Where A.Idproduttore = Inidproduttore And
                    A.Idstatoarticolo != Costanti.Stat_Art_Shipping And
                    A.Idarticolo = G.Idarticolo And
                    g.giacenza > 0;

                    Return Result;
                    end;

                    procedure codificaArticoli is
                    Cursor cArticoli is
                    Select a.rowid id,
                    a.idproduttore
                    From articoli a,
                    produttori p
                    Where a.idproduttore = p.idproduttore And
                    a.codiceSillaro is null And
                    p.prefissocodice is not null;

                    appProduttore produttori%rowtype;
                    begin
                    For rArticoli in cArticoli Loop
                    appProduttore := dao_select.produttori(rArticoli.idProduttore);
                    appProduttore.progressivo := appProduttore.progressivo + 1;

                    --dbms_output.put_line (appProduttore.Prefissocodice || '.' ||
                    trim(replace(to_char(appProduttore.progressivo, '000,00'), ',', '.')));

                    update articoli
                    set codiceSillaro = appProduttore.Prefissocodice || '.' ||
                    trim(replace(to_char(mod(appProduttore.progressivo, 100000), '000,00'), ',', '.'))
                    where rowid = rArticoli.id;

                    dao_update.produttori(appProduttore, appProduttore);
                    End Loop;
                    end;


                    procedure classifica is
                    Cursor cProduttori is
                    Select *
                    From produttori
                    Order By ordinamento;

                    i integer := 1;
                    begin

                    For rProduttori in cProduttori loop
                    if rProduttori.ordinamento is not null then
                    rProduttori.ordinamento := i * 10;
                    i := i + 1;
                    end if;

                    dao_update.produttori(rProduttori, rProduttori);
                    End Loop;

                    end;

                    end;

Package.SourceSpec:
package pck_pulizie is

