                    procedure DataQuality (inIDLogElaborazione in integer);
                    end;

Package.SourceBody:
package body pck_pulizie is

                    procedure PuliziaLogElaborazioni (inIDLogElaborazione in integer) is
                    AppMaxIDLogElaborazioni integer;
                    begin
                    Select max(l.idlogelaborazione)
                    Into AppMaxIDLogElaborazioni
                    From LogElaborazioni l
                    Where l.datainizio < trunc(sysdate) - ggLogElaborazioni;

                    -- Logelaborazionidettaglio
                    delete Logelaborazionidettaglio where idlogelaborazione <= AppMaxIDLogElaborazioni;
                    logging.addinformation(inIDLogElaborazione, 'Logelaborazionidettaglio: ' || sql%rowcount);

                    -- Logelaborazioni
                    delete Logelaborazioni where idlogelaborazione <= AppMaxIDLogElaborazioni;
                    logging.addinformation(inIDLogElaborazione, 'Logelaborazioni: ' || sql%rowcount);
                    end;

                    procedure PuliziaImport (inIDLogElaborazione in integer) is
                    begin
                    -- import_articoli
                    execute immediate 'truncate table import_articoli';
                    logging.addinformation(inIDLogElaborazione, 'import_articoli');

                    -- import_oem
                    execute immediate 'truncate table import_oem';
                    logging.addinformation(inIDLogElaborazione, 'import_oem');

                    -- import_schedetecniche
                    execute immediate 'truncate table import_schedetecniche';
                    logging.addinformation(inIDLogElaborazione, 'import_schedetecniche');

                    -- import_sistemi
                    execute immediate 'truncate table import_sistemi';
                    logging.addinformation(inIDLogElaborazione, 'import_sistemi');

                    -- import_sistemiarticoli
                    execute immediate 'truncate table import_sistemiarticoli';
                    logging.addinformation(inIDLogElaborazione, 'import_sistemiarticoli');

                    -- kingston_products
                    execute immediate 'truncate table kingston_products';
                    logging.addinformation(inIDLogElaborazione, 'kingston_products');

                    -- kingston_sysgroups
                    execute immediate 'truncate table kingston_sysgroups';
                    logging.addinformation(inIDLogElaborazione, 'kingston_sysgroups');

                    -- kingston_sysmemfeatures
                    execute immediate 'truncate table kingston_sysmemfeatures';
                    logging.addinformation(inIDLogElaborazione, 'kingston_sysmemfeatures');

                    -- kingston_sysnotes
                    execute immediate 'truncate table kingston_sysnotes';
                    logging.addinformation(inIDLogElaborazione, 'kingston_sysnotes');

                    -- kingston_sysparts
                    execute immediate 'truncate table kingston_sysparts';
                    logging.addinformation(inIDLogElaborazione, 'kingston_sysparts');

                    -- kingston_sysschema
                    execute immediate 'truncate table kingston_sysschema';
                    logging.addinformation(inIDLogElaborazione, 'kingston_sysschema');

                    -- kingston_systems
                    execute immediate 'truncate table kingston_systems';
                    logging.addinformation(inIDLogElaborazione, 'kingston_systems');

                    -- kingston_systems
                    execute immediate 'truncate table kingston_cpuchipsets';
                    logging.addinformation(inIDLogElaborazione, 'kingston_cpuchipsets');
                    end;

                    Procedure Puliziacms (Inidlogelaborazione In Integer) Is
                    Begin
                    -- cms_immagini
                    Logging.Addinformation(Inidlogelaborazione, 'Cms_Immagini');
                    Delete Cms_Immagini
                    Where Idimmagine In (Select Idimmagine From Cms_Immagini
                    minus
                    (Select Idimmagine From Articoli
                    Union
                    Select Idimmagine From Cms_Pagine
                    Union
                    Select Idimmagine From Cms_Sezioni
                    Union
                    Select Idimmagine From Gruppi
                    Union
                    Select Idimmagine From Produttori
                    Union
                    Select Idimmagine From Sottotipologie
                    Union
                    Select Idimmagine From Tipologie)
                    );
                    Logging.Addinformation(Inidlogelaborazione, ' Cancellati ' || sql%rowcount || 'record');

                    -- Immagini Tipologie
                    Logging.Addinformation(Inidlogelaborazione, 'Immagini Tipologie');
                    Update Tipologie t
                    set idimmagine = (select max(idimmagine) from View_Articoli a where a.Idtipologia = t.Idtipologia)
                    Where Idimmagine Is Null And
                    Ordinamento Is Not Null;
                    Logging.Addinformation(Inidlogelaborazione, ' Aggiornati ' || sql%rowcount || 'record');

                    -- Immagini SottoTipologie
                    Logging.Addinformation(Inidlogelaborazione, 'Immagini SottoTipologie');
                    Update Sottotipologie St
                    set idimmagine = (select max(idimmagine) from View_Articoli a where a.Idtipologia = st.Idtipologia)
                    Where Idimmagine Is Null And
                    Ordinamento is not null;
                    Logging.Addinformation(Inidlogelaborazione, ' Aggiornati ' || Sql%Rowcount || 'record');

                    end;

                    Procedure PuliziaNews (Inidlogelaborazione In Integer) Is
                    i integer;

                    Begin
                    -- Revisione Top News - Portale
                    Logging.Addinformation(Inidlogelaborazione, 'Ordinamento top news - Portale');
                    Update News
                    Set FlagPortale = 'N'
                    Where Idnews In (Select Idnews
                    from (Select N.*,
                    Rownum Riga
                    from (select idnews
                    from news_dec_sezioni s, news n
                    where s.codsezione = n.codsezione and
                    n.FlagPortale = 'S' and
                    n.FlagPubblicato = 'S'
                    order by data desc, idnews desc) n
                    ) where riga > numtopnews
                    );

                    Logging.Addinformation(Inidlogelaborazione, 'Nascoste ' || sql%rowcount || ' top news - Portale');


                    -- Revisione Top News - NewsByIt
                    Logging.Addinformation(Inidlogelaborazione, 'Ordinamento top news - NewsByIt');
                    Update News
                    Set Flaginevidenza = 'N'
                    Where Idnews In (Select Idnews
                    from (Select N.*,
                    Rownum Riga
                    from (select idnews
                    from news_dec_sezioni s, news n
                    where s.codsezione = n.codsezione and
                    n.flaginevidenza = 'S'
                    order by data desc, idnews desc) n
                    ) where riga > numtopnews
                    );

                    Logging.Addinformation(Inidlogelaborazione, 'Nascoste ' || sql%rowcount || ' top news - NewsByIt');

                    -- News Correlate
                    Insert Into News_Correlate
                    Select Idnewscorrelata, Idnews
                    From News_Correlate
                    minus
                    Select Idnews, Idnewscorrelata
                    From News_Correlate;

                    Logging.Addinformation(Inidlogelaborazione, 'Inserite ' || sql%rowcount || ' news correlate');

                    -- News Correlate x articolo
                    Insert Into News_Correlate
                    (Idnews, Idnewscorrelata)
                    Select Na1.Idnews,
                    Na2.Idnews
                    From News_Articoli Na1, News_Articoli Na2
                    Where Na1.Idarticolo = Na2.Idarticolo And
                    Na1.Idnews != Na2.Idnews
                    Minus
                    Select Nc.Idnews,
                    Nc.Idnewscorrelata
                    From News_Correlate Nc;

                    Logging.Addinformation(Inidlogelaborazione, 'Inserite ' || Sql%Rowcount || ' news correlate per
                    articolo');

                    end;

                    Procedure PuliziaPromozioni (Inidlogelaborazione In Integer) Is
                    Begin
                    Delete Promozioni Where Datafinevalidita < Sysdate - 60;
                    Logging.Addinformation(Inidlogelaborazione, 'Cancellate ' || sql%rowcount || ' Promozioni');
                    End;


                    Procedure Puliziaprezzi (Inidlogelaborazione In Integer) Is
                    Appprezzivendita Prezzivendita%Rowtype;
                    appPrezziAcquisto PrezziAcquisto%rowtype;

                    Cursor CprezziVendita Is
                    Select *
                    From Prezzivendita
                    Order By Idarticolo, Datainiziovalidita;

                    Cursor CprezziAcquisto Is
                    Select *
                    From Prezziacquisto
                    Order By idArticolo, idFornitore, dataInizioValidita;

                    Begin

                    logging.addinformation(inidlogelaborazione, ' Pulizia prezzi vendita');
                    appPrezziVendita.idArticolo := 0;
                    For Rprezzi In CprezziVendita Loop
                    If Appprezzivendita.Idarticolo = Rprezzi.Idarticolo And Nvl(Appprezzivendita.Prezzo, 0) =
                    Nvl(Rprezzi.Prezzo, 0) And Nvl(Appprezzivendita.Prezzonetto, 0) = Nvl(Rprezzi.Prezzonetto, 0) Then
                    dao_delete.prezziVendita(Rprezzi);

                    Appprezzivendita.Datafinevalidita := Rprezzi.Datafinevalidita;
                    Dao_Update.Prezzivendita(Appprezzivendita, Appprezzivendita);
                    Else
                    Appprezzivendita := Rprezzi;
                    End If;
                    End Loop;

                    Logging.Addinformation(Inidlogelaborazione, ' Pulizia prezzi acquisto');
                    Appprezziacquisto.Idarticolo := 0;
                    appPrezziAcquisto.idFornitore := 0;
                    For Rprezzi In Cprezziacquisto Loop
                    If AppprezziAcquisto.Idarticolo = Rprezzi.Idarticolo And AppprezziAcquisto.idFornitore =
                    Rprezzi.idFornitore And Nvl(AppprezziAcquisto.Prezzo, 0) = Nvl(Rprezzi.Prezzo, 0) Then
                    dao_delete.prezziAcquisto(Rprezzi);

                    AppprezziAcquisto.Datafinevalidita := Rprezzi.Datafinevalidita;
                    Dao_Update.PrezziAcquisto(AppprezziAcquisto, AppprezziAcquisto);
                    Else
                    AppprezziAcquisto := Rprezzi;
                    End If;
                    End Loop;
                    End;

                    procedure Riorganizza (inIDLogElaborazione in integer) is
                    begin
                    logging.addinformation(inidlogelaborazione, ' Sistema l''ordinamento delle sotto tipologie e
                    tipologie');
                    Pck_Gruppi.classifica;

                    --logging.addinformation(inidlogelaborazione, ' Riorganizza tipologie e sotto tipologie eliminando
                    le obsolete');
                    --Pck_Gruppi.riorganizza;
                    end;

                    procedure PuliziaSistemi (inIDLogElaborazione in integer) is
                    begin
                    Logging.Addinformation(Inidlogelaborazione, 'NominativiOem - Obsoleti');
                    delete nominativioem where idoem in (select idoem from oem where Flagobsoleto = 'S');

                    Logging.Addinformation(Inidlogelaborazione, 'Oem - Obsoleti');
                    delete oem where Flagobsoleto = 'S';
                    end;

                    procedure Pulizia (inIDLogElaborazione in integer) is
                    begin
                    PuliziaLogElaborazioni (inIDLogElaborazione);
                    PuliziaImport (Inidlogelaborazione);
                    PuliziaCms (inIDLogElaborazione);

                    PuliziaNews (Inidlogelaborazione);
                    PuliziaPromozioni (Inidlogelaborazione);
                    PuliziaPrezzi (Inidlogelaborazione);
                    PuliziaSistemi (Inidlogelaborazione);

                    Riorganizza (Inidlogelaborazione);
                    end;

                    procedure DataQuality (inIDLogElaborazione in integer) is
                    begin
                    -- Articoli - Descrizione non ASCII
                    Logging.Addinformation(Inidlogelaborazione, 'Articoli - Descrizione non ASCII');
                    update articoli
                    set descrizione = lib.getAsciiStr(descrizione)
                    where descrizione != lib.getAsciiStr(descrizione);

                    Logging.Addinformation(Inidlogelaborazione, ' Aggiornati ' || Sql%Rowcount || ' articoli');

                    -- Articoli - Codice Produttore non ASCII
                    Logging.Addinformation(Inidlogelaborazione, 'Articoli - Codice Produttore non ASCII');
                    update articoli
                    set CodiceProduttore = lib.getAsciiStr(CodiceProduttore)
                    where CodiceProduttore != lib.getAsciiStr(CodiceProduttore);

                    Logging.Addinformation(Inidlogelaborazione, ' Aggiornati ' || Sql%Rowcount || ' articoli');

                    -- Articoli - Codici OEM non ASCII
                    Logging.Addinformation(Inidlogelaborazione, 'Articoli - Codici OEM non ASCII');
                    update articoli
                    set CodiciOEM = lib.getAsciiStr(CodiciOEM)
                    where CodiciOEM != lib.getAsciiStr(CodiciOEM);

                    Logging.Addinformation(Inidlogelaborazione, ' Aggiornati ' || Sql%Rowcount || ' articoli');
                    end;

                    end;

