Package.SourceBody:
package body pck_sistemi is
procedure aggiornaObsoleti (Inidlogelaborazione In Integer) Is begin Update sistemi set flagMemorie = 'N', flagBatterie = 'N', flagAlimentatori = 'N';
-- Memorie Update sistemi Set flagMemorie = 'S' Where idSistema in (select distinct sa.idsistema From tipologie t, Sottotipologie st, articoli a, sistemiarticoli sa Where a.idsottotipologia = st.idsottotipologia And st.idtipologia = t.idtipologia And a.idarticolo = sa.idarticolo And t.idgruppo in (costanti.grp_memorie, costanti.grp_flash) And a.idstatoarticolo = 1);
Logging.addinformation(Inidlogelaborazione, 'Sistemi con Memorie: ' || sql%rowcount);
-- Batterie Update sistemi Set flagBatterie = 'S' Where idSistema in (select distinct sa.idsistema From tipologie t, Sottotipologie st, articoli a, sistemiarticoli sa Where a.idsottotipologia = st.idsottotipologia And st.idtipologia = t.idtipologia And a.idarticolo = sa.idarticolo And t.idgruppo = costanti.grp_batterie And A.Idstatoarticolo = 1);
Logging.Addinformation(Inidlogelaborazione, 'Sistemi con Batterie: ' || Sql%Rowcount);
-- Alimentatori Update sistemi Set flagAlimentatori = 'S' Where idSistema in (select distinct sa.idsistema From tipologie t, Sottotipologie st, articoli a, sistemiarticoli sa Where a.idsottotipologia = st.idsottotipologia And st.idtipologia = t.idtipologia And a.idarticolo = sa.idarticolo And t.idgruppo = costanti.grp_alimentatori And A.Idstatoarticolo = 1);
Logging.Addinformation(Inidlogelaborazione, 'Sistemi con Alimentatori: ' || Sql%Rowcount);
Delete Sistemi Where idsistema in (Select idsistema from sistemi minus Select idsistema from sistemiArticoli);
Logging.Addinformation(Inidlogelaborazione, 'Eliminati ' || Sql%Rowcount || ' sistemi obsoleti');
-- Obsolescenza OEM Update Oem set Flagobsoleto = 'N';
Update Oem Set Flagobsoleto = 'S' Where Idoem In (Select Idoem From Oem minus Select Idoem From Sistemi where Flagobsoleto = 'N');
end;
-- Unisce due sistemi procedure UnisciSistemi (InIDSistemaPrincipale in integer, InIDSistemaSecondario in integer) is begin -- SistemiArticoli Update SistemiArticoli Set IDSistema = InIDSistemaPrincipale Where IDSistema = InIDSistemaSecondario And IDarticolo in (Select IDarticolo from SistemiArticoli where IDSistema = InIDSistemaSecondario minus Select IDarticolo from SistemiArticoli where IDSistema = InIDSistemaPrincipale );
delete SistemiArticoli where IDSistema = InIDSistemaSecondario; delete Sistemi where IDSistema = InIDSistemaSecondario;
commit; end;
-- Unisce due sistemi Procedure Uniscioem (Inidoemprincipale In Integer, Inidoemsecondario In Integer) Is Begin Update Nominativioem Set Idoem = Inidoemprincipale where Idoem = Inidoemsecondario;
Update Sistemi Set Idoem = Inidoemprincipale where Idoem = Inidoemsecondario;
delete oem where Idoem = Inidoemsecondario; end;
end;

