package body pck_sito As

                    Procedure aggiornaAccount (inIdUtente in integer) is
                    appUtenti Sito_Utenti%rowtype;
                    Begin
                    Apputenti := Dao_Select.Sito_Utenti(Inidutente);
                    appUtenti.userid := lib_sicurezza.genPassword(1);
                    Apputenti.Password := lib_sicurezza.genPassword(2);
                    appUtenti.datamodificapassword := sysdate;

                    dao_update.Sito_Utenti (appUtenti, appUtenti);
                    End;

                    Procedure eliminaAccount (inIdUtente in integer) is
                    Begin
                    update sito_tracepagine
                    set idutente = null
                    Where Idutente = Inidutente;

                    delete Sito_Utenti where idutente = inIdUtente;
                    End;


                    Procedure AggiornaStatisticheSito is
                    appIdLogElaborazione integer;
                    begin
                    appIdLogElaborazione := dao_sequences.seq_idlogelaborazione();
                    Logging.Startlog(appIdLogElaborazione, 'Aggiornamento Statistiche');
                    AggiornaStatisticheSito (appIdLogElaborazione);
                    end;

                    Procedure AggiornaStatisticheSito (inIDlogElaborazione in integer) is
                    minData date;

                    begin
                    -- Inizializzazioni
                    Select trunc(min(dataora))
                    Into Mindata
                    From sito_tracepagine;

                    -- Pulisco la sintesi
                    Logging.Addinformation(inIDlogElaborazione, 'Pulisco la sintesi');
                    delete dwh_sitoPagine where Data >= minData;

                    -- Inserisco i dati aggiornati
                    Logging.Addinformation(inIDlogElaborazione, 'Inserisco i dati aggiornati');
                    Insert Into Dwh_Sitopagine
                    (Data, Sito, PagineLogged, Paginetotali)
                    Select Trunc(P.Dataora) Data,
                    p.Sito,
                    Sum(decode(idutente, null, 0, 2, 0, 1)) Paginelogged,
                    Count(*) Paginetotali
                    From Sito_Tracepagine P
                    Group By p.Sito, Trunc(P.Dataora);

                    Delete Sito_Tracepagine Where Dataora < Trunc(Sysdate - GG_ONLINE);

                    exception
                    when others then
                    rollback;
                    Logging.Adderror (inIdLogElaborazione, sqlerrm);
                    Logging.Abortlog (inIdLogElaborazione);
                    commit;
                    end;

                    function Existsofferte (Inidcliente In Integer) return boolean Is
                    App integer;

                    Begin
                    Select 1
                    Into App
                    From Offerteclienti
                    where idcliente = inidcliente and
                    Trunc(Sysdate) <= Datafinevalidita And
                    rownum < 2;

                    Return True;
                    Exception
                    When No_Data_Found Then
                    return false;
                    End;

                    function ExistsLicenze (Inidcliente In Integer) return boolean Is
                    Begin
                    return false;
                    End;

                    function getidaforisma return integer is
                    cursor cAforisma is
                    select *
                    from aforismi
                    order by decode(dataultimapublicazione, trunc(sysdate), -2, null, -1, dataultimapublicazione -
                    to_date('01/01/1900', 'dd/mm/yyyy'));

                    begin
                    for rAforisma in cAforisma loop
                    rAforisma.Dataultimapublicazione := trunc(sysdate);
                    dao_update.Aforismi (rAforisma, rAforisma);

                    return rAforisma.idAforisma;
                    end loop;
                    end;


                    function getIdBarzelletta return integer is
                    cursor cBarzellette is
                    select *
                    from barzellette
                    order by decode(dataultimapublicazione, trunc(sysdate), -2, null, -1, dataultimapublicazione -
                    to_date('01/01/1900', 'dd/mm/yyyy'));

                    begin
                    for rBarzellette in cBarzellette loop
                    rBarzellette.Dataultimapublicazione := trunc(sysdate);
                    dao_update.Barzellette (rBarzellette, rBarzellette);

                    return rBarzellette.idBarzelletta;
                    end loop;
                    end;

                    -- Info Sito - Spedizioni nel giorno
                    Function Existsspedizioni(Inidcliente In Integer) Return Boolean Is
                    app Integer;
                    Begin
                    Select 1
                    Into App
                    From Documenti
                    Where idCliente = Inidcliente And
                    dataspedizione >= trunc(sysdate-7) And
                    rownum < 2;

                    Return True;

                    Exception
                    When No_Data_Found Then
                    Return False;
                    End;

                    -- Info Sito - Esistenza backorder
                    Function Existsbackorder(Inidcliente In Integer) Return Boolean Is
                    app Integer;
                    Begin
                    Select 1
                    Into App
                    From View_Backorder
                    Where Idcliente = Inidcliente And
                    Quantitabackorder > 0 And
                    rownum < 2;

                    Return True;

                    Exception
                    When No_Data_Found Then
                    Return False;
                    End;

                    end;

