Package Pck_Special Is

                    Function GetImportoNetto(InidSpecial Integer) Return Float;
                    Function GetIVA(InidSpecial Integer) Return Float;
                    Function GetImporto(InidSpecial Integer) Return Float;

                    End;

Package.SourceBody:
package body pck_Special is

                    Function GetimportoNetto(InidSpecial Integer) Return Float Is
                    result float;
                    Begin
                    Select Sum(Ro.Quantita * Ro.specialVendita) ImportoNetto
                    Into result
                    From RigheSpecial Ro
                    Where Ro.idSpecial = InidSpecial;

                    return result;
                    end;

                    Function GetIVA(InidSpecial Integer) Return Float Is
                    result float;
                    Begin
                    Select Sum(round(Ro.Quantita * Ro.specialVendita * A.Aliquota / 100, 2)) IVA
                    Into Result
                    From RigheSpecial Ro, Dec_Aliquote A
                    Where Ro.Codaliquota = A.Codaliquota And
                    Ro.idSpecial = InidSpecial;

                    Return Result;
                    end;

                    Function GetImporto(InidSpecial Integer) Return Float Is
                    result float;
                    Begin
                    Select Sum(Ro.Quantita * Ro.specialVendita + round(Ro.Quantita * Ro.specialVendita * A.Aliquota /
                    100, 2)) Importo
                    Into result
                    From RigheSpecial Ro, Dec_Aliquote A
                    Where Ro.Codaliquota = A.Codaliquota And
                    Ro.idSpecial = InidSpecial;

                    Return Result;
                    end;
                    end;

