Package.SourceBody:
package body sito_Funzioni is
                    function getIdImmagineTipologia (inIdProduttore in integer, inIdTipologia in integer) return integer
                    is
                    Cursor cARticoli is
                    Select a.idimmagine
                    From tipologie t, SottoTipologie st, Articoli a
                    where a.idSottoTipologia = st.idSottoTipologia And
                    st.idTipologia = t.idTipologia And
                    a.idimmagine is not null And
                    a.idstatoarticolo = 1 And
                    a.codicesillaro is not null And
                    a.idProduttore = inIdProduttore And
                    st.idTipologia = inIdTipologia
                    Order by idarticolo desc;

                    begin
                    For rARticoli in cARticoli Loop
                    return rARticoli.idimmagine;
                    End Loop;

                    return null;
                    end;

                    function getIdImmagineSottoTipologia (inIdProduttore in integer, inIdSottoTipologia in integer)
                    return integer is
                    Cursor cARticoli is
                    Select a.idimmagine
                    From tipologie t, SottoTipologie st, Articoli a
                    where a.idSottoTipologia = st.idSottoTipologia And
                    st.idTipologia = t.idTipologia And
                    a.idimmagine is not null And
                    a.idstatoarticolo = 1 And
                    a.codicesillaro is not null And
                    a.idProduttore = inIdProduttore And
                    a.idSottoTipologia = inIdSottoTipologia
                    Order by idarticolo desc;

                    begin
                    For rARticoli in cARticoli Loop
                    return rARticoli.idimmagine;
                    End Loop;

                    return null;
                    end;
                    end;
